import { dbUrl } from "./index";
import axiosWithAuth from "../contexts/withAuth";

export const GET_INSPECTION_FORM_START = "GET_INSPECTION_FORM_START";
export const GET_INSPECTION_FORM_SUCCESS = "GET_INSPECTION_FORM_SUCCESS";
export const GET_INSPECTION_FORM_FAILURE = "GET_INSPECTION_FORM_FAILURE";

export const getInspectionForm = () => async (dispatch) => {
  dispatch({ type: GET_INSPECTION_FORM_START });

  const axiosAuth = await axiosWithAuth();

  return axiosAuth
    .get(`${dbUrl}/inspection_form`)
    .then((res) => {
      dispatch({
        type: GET_INSPECTION_FORM_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: GET_INSPECTION_FORM_FAILURE,
        payload: err,
      });
    });
};
