import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import EmployeeTable from './EmployeeTable';

import '../../styles/employee-dashboard.scss';

const CurrentEmployeesContent = () => {
    const [employees, setEmployees] = useState([]);
    const getEmployees = useSelector(state => state.getEmployeesReducer.employees);

    useEffect(() => {
        const currentEmployees = [];
        console.log(getEmployees.employees)

        if(getEmployees) {
            getEmployees.employees.map(employee => {
                if(employee.active === true) {
                    currentEmployees.push(employee);
                };
            });
        }
        console.log(currentEmployees)
        setEmployees(getEmployees.employees);
    }, [getEmployees]);

    return (
        <div className="current-employees-page">
            <h1>Current Employees</h1>
            {employees ? <EmployeeTable props={employees} /> : null}
        </div>
    );
};

export default CurrentEmployeesContent;
