import React from 'react';
import { useSelector } from 'react-redux';
import { Route, Redirect } from 'react-router-dom';

const EmpPrivateRoute = ({ component: Component, ...rest }) => {
    const userType = useSelector(state => state.persistUserReducer.user.userType);

    return (
        <Route
            {...rest}
            render = { props => {
                if(localStorage.getItem('token') && userType === 'employee') {
                    return <Component {...props} />
                } else {
                    return <Redirect to="/" />
                }
            }}
        />
    );
};

export default EmpPrivateRoute;
