import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { openWorkOrders, closeWorkOrders } from '../../actions/workOrderToggle';
import { useLocation } from 'react-router-dom';
import { Button, ButtonGroup } from 'reactstrap';
import '../../styles/sidebar.scss';

const WorkOrderToggle = () => {
    const [hidden, setHidden] = useState(false);
    const open = useSelector(state => state.workOrderToggleReducer.open);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, location)

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(!hidden);
        }
    }

    return (
        <div className="task-btns">
            {hidden ? <ButtonGroup className="button-group">
                <Button onClick={() => dispatch(openWorkOrders())} active={open === true}>Open</Button>
                <Button onClick={() => dispatch(closeWorkOrders())} active={open === false}>Closed</Button>
            </ButtonGroup> : null}
        </div>
    )
}

export default WorkOrderToggle;
