import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  FormFeedback,
} from "reactstrap";

import "../../styles/sidebar.scss";

const AddEventBtn = () => {
  const [event, setEvent] = useState({
    title: "",
    startDate: "",
    endDate: "",
    startTime: "",
    endTime: "",
    allDay: false,
    recurring: false,
    daysOfWeek: [],
    extendedProps: {
      location: "",
      inviteAll: false,
      invitees: [],
    },
  });
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    if (isOpen) {
      setEvent({
        title: "",
        startDate: "",
        endDate: "",
        startTime: "",
        endTime: "",
        allDay: false,
        recurring: false,
        daysOfWeek: [],
        extendedProps: {
          location: "",
          inviteAll: false,
          invitees: [],
          description: "",
        },
      });
    }

    setIsOpen(!isOpen);
  };

  const handleChange = () => {};

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="add-event-div">
      <Button color="danger" onClick={toggle}>
        Add Event
      </Button>

      <Modal isOpen={isOpen} toggle={toggle}>
        <ModalHeader toggle={toggle}>Add Event</ModalHeader>
        <ModalBody>
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Title</Label>
              <Input
                type="text"
                name="title"
                onChange={handleChange}
                // invalid={errors.title ? true : false}
              />
              {/* <FormFeedback>{errors.title}</FormFeedback> */}
            </FormGroup>
            <div className="start-end-date-div">
              <FormGroup>
                <Label>Start Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="startDate"
                  onChange={handleChange}
                  // invalid={errors.startDate ? true : false}
                />
                {/* <FormFeedback>{errors.startDate}</FormFeedback> */}
              </FormGroup>
              <FormGroup>
                <Label>End Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="endDate"
                  onChange={handleChange}
                  // invalid={errors.endDate ? true : false}
                />
                {/* <FormFeedback>{errors.endDate}</FormFeedback> */}
              </FormGroup>
            </div>
            <div className="start-end-time-div">
              <FormGroup>
                <Label>Start Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="startTime"
                  onChange={handleChange}
                />
              </FormGroup>
              <FormGroup>
                <Label>End Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="endTime"
                  onChange={handleChange}
                  // invalid={errors.endTime ? true : false}
                />
                {/* <FormFeedback>{errors.endTime}</FormFeedback> */}
              </FormGroup>
            </div>
            <FormGroup check>
              <Label>Recurring</Label><br/>
              <Label check>
                <Input type="checkbox" name="recurring" />{' '}
                Yes
              </Label>
            </FormGroup>
          </Form>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default AddEventBtn;
