import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
} from 'reactstrap';
import { NavLink as RRNavLink, useHistory } from 'react-router-dom';
import Logo from '../../assets/logo.png';

import '../../styles/employee-navbar.scss';

const EmployeeNavbar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const user = useSelector(state => state.persistUserReducer.user);
    let history = useHistory();

    const toggle = () => {
        setIsOpen(!isOpen);
    }

    const logout = e => {
        e.preventDefault();
        localStorage.clear();
        history.push('/');
    }

    return (
        <div className="nav-container">
            <Navbar className="employee-navbar" expand="md">
                <NavbarBrand href="/">
                    <div className="logo-box">
                        <img className="logo" src={ Logo } alt="rocky mountain lifts" />
                    </div>
                </NavbarBrand>
                <Nav className="ml-auto" navbar>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard" exact>
                            Tasks
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/workorders">
                            Work Orders
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/customers">
                            Customers
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/files">
                            Files
                        </NavLink>
                    </NavItem>
                    {user.tierLevel === 3 ? <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/employees">
                            Employees
                        </NavLink>
                    </NavItem> : null}
                    <NavItem>
                        <div className="divider" />
                    </NavItem>
                    <NavItem>
                        <Dropdown isOpen={isOpen} toggle={toggle}>
                            <DropdownToggle tag="span" className="nav-link">{user.firstName} {user.lastName}</DropdownToggle>
                            <DropdownMenu right>
                                <DropdownItem onClick={logout}>Logout</DropdownItem>
                            </DropdownMenu>
                        </Dropdown>
                    </NavItem>
                </Nav>
            </Navbar>
        </div>
    );
};

export default EmployeeNavbar;
