import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback
} from 'reactstrap';
import Select from 'react-select';
import { useLocation } from 'react-router-dom';
import { addTask } from '../../actions/addTask';
import { getUsers } from '../../actions/getUsers';

import '../../styles/sidebar.scss';

const AddTaskBtn = () => {
    const [task, setTask] = useState({
        taskDescription: '',
        taskDate: '',
        taskTime: '',
        employeeId: ''
    });
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const employeeArray = [];
    const [employees, setEmployees] = useState([]);
    const getEmployees = useSelector(state => state.getUsersReducer.users);
    const location = useLocation();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers());
    }, []);

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    useEffect(() => {
        if(getEmployees.users) {
            getEmployees.users.map(user => {
                if(user.userType === 'employee' && user.active === true) {
                    employeeArray.push({
                        label: `${user.firstName} ${user.lastName}`,
                        value: user.userId,
                        name: 'employeeId',
                        jobTitle: user.jobTitle
                    });
                };
            });

            setEmployees(employeeArray);
        };
    }, [getUsersFromState]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard' || location.pathname === '/e_dashboard/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        if(isOpen) {
            setTask({
                taskDescription: '',
                taskDate: '',
                taskTime: '',
                employeeId: ''
            });
        }
        setIsOpen(!isOpen);
    };

    return (
        <div className="add-task-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Task</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Add Task</ModalHeader>
                <ModalBody>
                    <FormGroup>
                        <Label>Task</Label>
                        <Input type="textarea" />
                    </FormGroup>
                    <div>
                        <FormGroup>
                            <Label>Date</Label>
                            <Input type="date" placeholder="date placeholder" />
                        </FormGroup>
                        <FormGroup>
                            <Label>Time</Label>
                            <Input type="time" placeholder="time placeholder" />
                        </FormGroup>
                    </div>
                    <FormGroup>
                        <Label>Assigned To</Label>
                        <Select />
                        <Input type="hidden" disabled />
                    </FormGroup>
                </ModalBody>
                <ModalFooter>
                    <Button color="success">Submit</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default AddTaskBtn;
