import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const TasksListContent = () => {
    const [tasks, setTasks] = useState([]);
    const { getTasks } = useSelector(state => state.getTasksReducer.tasks);

    useEffect(() => {
        if(getTasks) {
            console.log(getTasks);
        }
    }, [getTasks.length]);

    console.log(tasks)

    return (
        <div>
        {tasks.length > 0 ? 
            tasks.forEach(task => (
                <h1>{task.title}</h1>
            )) :
            <h1>No tasks</h1>
        }
        </div>
    );
};

export default TasksListContent;
