import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import OpenWorkOrders from './OpenWorkOrders';
import { getWorkOrders } from '../../actions/getWorkOrders';

import '../../styles/employee-dashboard.scss';

const WorkOrdersContent = () => {
    const open = useSelector(state => state.workOrderToggleReducer.open);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getWorkOrders());
    }, []);

    return (
        <div className="content-page">
            {open ? <OpenWorkOrders /> : <h1>Work Orders - Closed</h1>}
        </div>
    );
};

export default WorkOrdersContent;
