import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input,
} from 'reactstrap';
import Select from 'react-select';
import { useLocation } from 'react-router-dom';
import { addWorkOrder } from '../../actions/addWorkOrder';

import '../../styles/sidebar.scss';

const AddWorkOrderBtn = () => {
    const [workOrder, setWorkOrder] = useState({
        customer: '',
        serviceDate: '',
        serviceTime: '',
        serviceDescription: '',
        serviceType: [],
        dispatcher: '',
        serviceManager: '',
        officeManager: '',
        crewMembers: [],
        prevJobNumber: '',
        notes: '',
        officeNotes: '',
        quote: ''
    });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state.addWorkOrderReducer.errors);
    const getSuccess = useSelector(state => state.addWorkOrderReducer.success);
    const [employees, setEmployees] = useState([]);
    const [serviceManagers, setServiceManagers] = useState([]);
    const [officeManagers, setOfficeManagers] = useState([]);
    const employeesArray = [];
    const serviceManagersArray = [];
    const officeManagersArray = [];
    const [customers, setCustomers] = useState([]);
    const customersArray = [];
    const getUsers = useSelector(state => state.getUsersReducer.users); 
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const [isOpenCheck, setIsOpenCheck] = useState(false);
    const [warnings, setWarnings] = useState({});
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const isFirstRun = useRef(true);
    useEffect(() => {
        if(isFirstRun.current) {
            isFirstRun.current = false;
            return;
        }
        setErrors(getErrors);
    }, [getErrors]);

    useEffect(() => {
        if(getSuccess === true) {
            setSuccess(true);
        };
    }, [getSuccess]);

    useEffect(() => {
        if(success === true) {
            setIsOpen(false);
            setErrors({})
            setSuccess(false);
            setWorkOrder({
                ...workOrder,
                customer: '',
                serviceDate: '',
                serviceTime: '',
                serviceDescription: '',
                serviceType: [],
                dispatch: '',
                crewMembers: [],
                prevJobNumber: '',
                notes: '',
                officeNotes: '',
                quote: ''
            });
        };
    }, [success]);

    useEffect(() => {
        if(getUsers.users) {
            getUsers.users.forEach(user => {
                if(user.userType === 'employee') {
                    employeesArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId,
                            name: 'crewMembers',
                            jobTitle: user.jobTitle
                        }
                    );
                } else {
                    customersArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId,
                            name: 'customer'
                        }
                    );
                };
            });
        };

        employeesArray.push({ label: 'General Contractor', value: 'General Contractor' });

        setEmployees(employeesArray);
        setCustomers(customersArray);

        employeesArray.forEach(employee => {
            if(employee.jobTitle === 'Service Manager') {
                employee.name = 'serviceManager';
                serviceManagersArray.push(employee);
                if(employee.label === 'Steve Bolton') {
                    setWorkOrder({
                        ...workOrder,
                        serviceManager: employee.value
                    });
                };
            } else if(employee.jobTitle === 'Office Manager') {
                employee.name = 'officeManager';
                officeManagersArray.push(employee);
                if(employee.label === 'Tena Hale') {
                    setWorkOrder({
                        ...workOrder,
                        officeManager: employee.value
                    });
                };
            };
        });

        setServiceManagers(serviceManagersArray);
        setOfficeManagers(officeManagersArray);
    }, [getUsers]);

    const toggle = e => {
        setIsOpen(!isOpen);
        setErrors({});
        setWarnings({});

        if(!isOpen) {
            setWorkOrder({
                customer: '',
                serviceDate: '',
                serviceTime: '',
                serviceDescription: '',
                serviceType: [],
                dispatcher: '',
                crewMembers: [],
                prevJobNumber: '',
                notes: '',
                officeNotes: '',
                quote: ''
            });
        };
    };

    const toggleCheck = e => {
        if(isOpenCheck) {
            setWarnings({});
        };
        setIsOpenCheck(!isOpenCheck);  
    };

    const handleChange = e => {
        setWorkOrder({
            ...workOrder,
            [e.target.name]: e.target.value
        });
    };

    const handleSelectChange = e => {
        setWorkOrder({
            ...workOrder,
            [e.name]: e.value
        });
    };

    const handleMultiSelectChange = e => {
        const values = [];
        let name = e[0].name;

        e.forEach(e => {
            values.push(e.value);
        })

        console.log(values)
        setWorkOrder({
            ...workOrder,
            [name]: values
        })
    };

    const handleSubmit = e => {
        e.preventDefault();

        const valid = warningCheck();

        if(valid) {
            dispatch(addWorkOrder(workOrder));
        } else {
            toggleCheck();
        };
    };

    const warningCheck = e => {
        let warningsObject = {};

        if(workOrder.prevJobNumber.trim() === '') {
            warningsObject.prevJobNumber = 'Previous Job Number';
        };
        if(workOrder.notes.trim() === '') {
            warningsObject.notes = 'Notes';
        };
        if(workOrder.officeNotes.trim() === '') {
            warningsObject.officeNotes = 'Office Notes';
        };
        if(workOrder.quote.trim() === '') {
            warningsObject.quote = 'Quote';
        };

        if(Object.keys(warningsObject).length === 0) {
            return true;
        } else {
            setWarnings(warningsObject);
            return false;
        };
    };

    const handleSubmitCheck = e => {
        e.preventDefault();
        setWarnings({});
        dispatch(addWorkOrder(workOrder));
        toggleCheck();
    };

    return (
        <div className={hidden ? "add-work-order-div": ""}>
            {hidden ? <Button color="success" onClick={toggle}>Create Work Order</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Create Work Order</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label for="customer">Customer</Label>
                            <Select options={customers} name="customer" onChange={handleSelectChange} className={errors.customer ? 'invalid' : ''} />
                            <Input type="hidden" disabled invalid={errors.customer ? true : false} />
                            <FormFeedback>{errors.customer}</FormFeedback>
                        </FormGroup>
                        <div className="datepicker-div">
                            <FormGroup>
                                <Label for="date">Date</Label>
                                <Input type="date" name="serviceDate" id="serviceDate" placeholder="date placeholder" onChange={handleChange} invalid={errors.serviceDate ? true : false} />
                                <FormFeedback>{errors.serviceDate}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="time">Time</Label>
                                <Input type="time" name="serviceTime" id="serviceTime" placeholder="time placeholder" onChange={handleChange} invalid={errors.serviceTime ? true : false} />
                                <FormFeedback>{errors.serviceTime}</FormFeedback>
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="serviceDescription">Service Description</Label>
                            <Input type="textarea" name="serviceDescription" id="serviceDescription" onChange={handleChange} invalid={errors.serviceDescription ? true : false} />
                            <FormFeedback>{errors.serviceDescription}</FormFeedback>
                        </FormGroup>
                        <FormGroup>
                            <Label for="serviceType">Service Type</Label>
                            <Select isMulti options={serviceTypes.map(type => ( { label: type, value: type, name: 'serviceType' } ))} name="serviceType" onChange={handleMultiSelectChange} className={errors.serviceType ? 'invalid' : ''} />
                            <Input type="hidden" disabled invalid={errors.serviceType ? true : false} />
                            <FormFeedback>{errors.serviceType}</FormFeedback>
                        </FormGroup>
                        <div className="manager-div">
                            <FormGroup>
                                <Label for="serviceManager">Service Manager</Label>
                                <Select options={serviceManagers} defaultValue={serviceManagers[0]} name="serviceManager" onChange={handleSelectChange} className={errors.serviceManager ? 'invalid' : ''} />
                                <Input type="hidden" disabled invalid={errors.serviceManager ? true : false} />
                                <FormFeedback>{errors.serviceManager}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="officeManager">Office Manager</Label>
                                <Select options={officeManagers} defaultValue={officeManagers[0]} name="officeManager" onChange={handleSelectChange} className={errors.officeManager ? 'invalid' : ''} />
                                <Input type="hidden" disabled invalid={errors.officeManager ? true : false} />
                                <FormFeedback>{errors.officeManager}</FormFeedback>
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="crewMembers">Crew Members</Label>
                            <Select isMulti options={employees} name="crewMembers" onChange={handleMultiSelectChange} className={errors.crewMembers ? 'invalid': ''} />
                            <Input type="hidden" disabled invalid={errors.crewMembers ? true : false} />
                            <FormFeedback>{errors.crewMembers}</FormFeedback>
                        </FormGroup>
                        <FormGroup>
                            <Label for="prevJobNumber">Previous Job Number</Label>
                            <Input type="text" name="prevJobNumber" id="prevJobNumber" onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="notes">Notes</Label>
                            <Input type="textarea" name="notes" id="notes" onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="officeNotes">Office Notes</Label>
                            <Input type="textarea" name="officeNotes" id="officeNotes" onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="quote">Quote</Label>
                            <Input type="file" name="file" id="file" onChange={handleChange} />
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" type="submit" onClick={handleSubmit}>Submit</Button>
                </ModalFooter>
            </Modal>
            <Modal isOpen={isOpenCheck} toggle={toggleCheck}>
                <ModalHeader toggle={toggleCheck}>Warning</ModalHeader>
                <ModalBody>
                    <span>The following fields were left blank:</span>
                    <ul>
                        {warnings ? Object.values(warnings).map(warning => (
                            <li>{warning}</li>
                        )) : null}
                    </ul>
                    <span>Are you sure you want to continue?</span>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" type="submit" onClick={handleSubmitCheck}>YES</Button>
                    <Button color="danger" onClick={toggleCheck}>NO</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default AddWorkOrderBtn;

const serviceTypes = [
    "Install",
    "Service",
    "Training",
    "Inspection",
    "Warranty"
];
