import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  Form,
  FormGroup,
  FormFeedback,
  Label,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Table,
} from "reactstrap";
import { getTasks } from "../../actions/getTasks";
import { getWorkOrders } from "../../actions/getWorkOrders";
import { getEmployees } from "../../actions/getEmployees";
import Select from "react-select";

import "../../styles/task.scss";

const Task = () => {
  const [tasks, setTasks] = useState([]);
  const fetchTasks = useSelector((state) => state.getTasksReducer.tasks);
  const [workOrders, setWorkOrders] = useState([]);
  const fetchWorkOrders = useSelector(
    (state) => state.getWorkOrdersReducer.workOrders
  );
  const [employees, setEmployees] = useState();
  const fetchEmployees = useSelector(
    (state) => state.getEmployeesReducer.employees
  );
  const [isOpen, setIsOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState();
  const [errors, setErrors] = useState({
    taskDescription: "",
    taskDate: "",
    taskTime: "",
    employeeId: "",
  });
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getTasks());
    dispatch(getWorkOrders());
    dispatch(getEmployees());
  }, []);

  useEffect(() => {
    setTasks(fetchTasks.tasks);
  }, [fetchTasks]);

  useEffect(() => {
    setWorkOrders(fetchWorkOrders.workOrders);
  }, [fetchWorkOrders]);

  useEffect(() => {
    const array = [];

    if (fetchEmployees.employees) {
      fetchEmployees.employees.forEach((employee) => {
        if (employee.active === true) {
          array.push({
            label: `${employee.firstName} ${employee.lastName}`,
            value: employee.userId,
            name: "employeeId",
            jobTitle: employee.jobTitle,
          });
        }
      });

      setEmployees(array);
    }
  }, [fetchEmployees]);

  const dateConverter = (date) => {
    const dateArray = date.split("-");
    return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
  };

  const timeConverter = (time) => {
    const timeArray = time.split(":");
    const hour = parseInt(timeArray[0]);
    let m;

    if (hour <= 11) {
      m = "AM";
    } else {
      m = "PM";
    }

    if (hour === 0) {
      const newHour = "12";
      timeArray[0] = newHour;
    } else if (hour <= 12) {
      const newHour = hour.toString();
      timeArray[0] = newHour;
    } else {
      const newHour = (hour - 12).toString();
      timeArray[0] = newHour;
    }

    return `${timeArray[0]}:${timeArray[1]} ${m}`;
  };

  const toggle = (e, task) => {
    console.log(task);
    setCurrentTask(task);
    setIsOpen(!isOpen);
  };

  const handleChange = (e) => {
    setCurrentTask({
      ...currentTask,
      [e.target.name]: e.target.value,
    });
  };

  const handleSelectChange = (e) => {
    setCurrentTask({
      ...currentTask,
      [e.name]: e.value,
    });
  };

  const handleSubmit = () => {};

  return (
    <div className="tasks-page">
      <h1>Tasks</h1>
      {tasks
        ? tasks.map((task) => (
            <Card
              onClick={(e) => toggle(e, task)}
              className="tasks-page-task-card"
            >
              <Table borderless>
                <thead>
                  <tr>
                    <th>Task</th>
                    <th>Date</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{task.taskDescription}</td>
                    <td>{dateConverter(task.taskDate)}</td>
                    <td>{timeConverter(task.taskTime)}</td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          ))
        : null}
        <h1>Work Orders</h1>
        {workOrders
          ? workOrders.map((workOrder) => (
            <Card className="tasks-page-task-card">
            <Table borderless>
                <thead>
                  <tr>
                    <th>Work Order #</th>
                    <th>Customer</th>
                    <th>Service Date</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{workOrder.workOrderId}</td>
                    <td>{workOrder.customerName}</td>
                    <td>{workOrder.serviceDate}</td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          ))
        : null}
      <Modal isOpen={isOpen} toggle={toggle}>
        <ModalHeader toggle={toggle}>Task</ModalHeader>
        <ModalBody>
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Task</Label>
              <Input
                type="textarea"
                name="taskDescription"
                value={currentTask ? currentTask.taskDescription : null}
                onChange={handleChange}
                invalid={errors.taskDescription ? true : false}
              />
              <FormFeedback>{errors.taskDescription}</FormFeedback>
            </FormGroup>
            <div>
              <FormGroup>
                <Label>Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="taskDate"
                  value={currentTask ? currentTask.taskDate : null}
                  onChange={handleChange}
                  invalid={errors.taskDate ? true : false}
                />
                <FormFeedback>{errors.taskDate}</FormFeedback>
              </FormGroup>
              <FormGroup>
                <Label>Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="taskTime"
                  value={currentTask ? currentTask.taskTime : null}
                  onChange={handleChange}
                  invalid={errors.taskTime ? true : false}
                />
                <FormFeedback>{errors.taskTime}</FormFeedback>
              </FormGroup>
            </div>
            <FormGroup>
              <Label>Assigned To</Label>
              <Select
                options={employees}
                defaultValue={currentTask ? {value: currentTask.employeeId} : null}
                onChange={handleSelectChange}
                className={errors.employeeId ? "invalid" : ""}
              />
              <Input
                type="hidden"
                disabled
                invalid={errors.employeeId ? true : false}
              />
              <FormFeedback>{errors.employeeId}</FormFeedback>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="danger">Edit</Button>
          <Button color="success">Complete</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default Task;
