import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormGroup,
    FormFeedback,
    Label,
    Input,
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddCustomerBtn = () => {
    const [customer, setCustomer] = useState({
        businessName: '',
        primaryContact: {
            firstName: '',
            lastName: ''
        },
        billingContact: {
            firstName: '',
            lastName: ''
        },
        primaryEmail: '',
        billingEmail: '',
        shopAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        billingAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
    });
    const [errors, setErrors] = useState({
        businessName: '',
        primaryContact: {
            firstName: '',
            lastName: ''
        },
        billingContact: {
            firstName: '',
            lastName: ''
        },
        primaryEmail: '',
        billingEmail: '',
        shopAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        billingAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
    });
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state);
    const getSuccess = useSelector(state => state);
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const isFirstRun = useRef(true);
    useEffect(() => {
        if(isFirstRun.current) {
            isFirstRun.current = false;
            return;
        }
        setErrors(getErrors);
    }, [getErrors]);

    useEffect(() => {
        if(getSuccess === true) {
            setSuccess(true);
        };
    }, [getSuccess]);

    useEffect(() => {
        if(success === true) {
            setIsOpen(false);
            setErrors({
                businessName: '',
                primaryContact: {
                    firstName: '',
                    lastName: ''
                },
                billingContact: {
                    firstName: '',
                    lastName: ''
                },
                primaryEmail: '',
                billingEmail: '',
                shopAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                billingAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
            })
            setSuccess(false);
            setCustomer({
                businessName: '',
                primaryContact: {
                    firstName: '',
                    lastName: ''
                },
                billingContact: {
                    firstName: '',
                    lastName: ''
                },
                primaryEmail: '',
                billingEmail: '',
                shopAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                billingAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
            });
        };
    }, [success]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/customers' || location.pathname === '/e_dashboard/customer/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        setIsOpen(!isOpen);
        setErrors({})
    };

    const handleChange = e => {
        setCustomer({
            ...customer,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = e => {
        e.preventDefault();
        // dispatch(registerCustomer(customer));
    };

    return (
        <div className="add-customer-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Customer</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Add Customer</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label for="businessName">Business Name</Label>
                            <Input type="text" name="businessName" onChange={handleChange} invalid={errors.businessName ? true : false} />
                            <FormFeedback>{errors.businessName}</FormFeedback>
                        </FormGroup>
                        <Label for="primary-contact">Primary Contact</Label>
                        <div className="primary-contact-name">
                            <FormGroup>
                                <Label for="firstName">First Name</Label>
                                <Input type="text" name="firstName" onChange={errors.primaryContact.firstName !== '' ? true : false}  />
                                <FormFeedback>{errors.primaryContact.firstName !== '' ? errors.primaryContact.firstName : null}</FormFeedback>
                            </FormGroup>
                        </div>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
};

export default AddCustomerBtn;
