import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddWorkOrderBtn = () => {
    const [hidden, setHidden] = useState(false);
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const toggle = e => {
        return;
    };

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className={hidden ? "work-order-div": ""}>
            {hidden ? <Button color="success" onClick={toggle}>Create Work Order</Button> : null}
        </div>
    );
};

export default AddWorkOrderBtn;
