import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormGroup,
    FormFeedback,
    Label,
    Input,
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddCustomerBtn = () => {
    const [hidden, setHidden] = useState(false);
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/customers' || location.pathname === '/e_dashboard/customer/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className="add-customer-div">
            {hidden ? <Button color="success">Add Customer</Button> : null}
        </div>
    );
};

export default AddCustomerBtn;
