import axiosWithAuth from '../components/Login/withAuth';
import { dbUrl } from './index';

export const SUBMIT_WORK_ORDER_REVIEW_START = 'SUBMIT_WORK_ORDER_REVIEW_START';
export const SUBMIT_WORK_ORDER_REVIEW_SUCCESS = 'SUBMIT_WORK_ORDER_REVIEW_SUCCESS';
export const SUBMIT_WORK_ORDER_REVIEW_FAILURE = 'SUBMIT_WORK_ORDER_REVIEW_FAILURE';

export const workOrderReviewSubmit = data => async dispatch => {
    dispatch({ type: SUBMIT_WORK_ORDER_REVIEW_START });

    return await axiosWithAuth()
        .put(`${dbUrl}/work_order`, data)
        .then(() => {
            dispatch({ 
                type: SUBMIT_WORK_ORDER_REVIEW_SUCCESS,
                payload: 'SUCCESS'
            });
        })
        .catch(err => {
            dispatch({
                type: SUBMIT_WORK_ORDER_REVIEW_FAILURE,
                payload: err.response.data
            });
        });
};
