import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import {
    TabContent,
    TabPane,
    Nav,
    NavItem,
    NavLink,
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback,
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Table,
    Card
} from 'reactstrap';
import Select from 'react-select';
import EmployeeNavbar from './EmployeeNavbar';
import EmployeeSidebar from './EmployeeSidebar';
import Footer from './Footer';
import { getCustomer } from '../../actions/getCustomer';
import { addEmployeeToCustomer } from '../../actions/addEmployeeToCustomer';
import { getCustomerTasks } from '../../actions/getCustomerTasks';
import { getUsers } from '../../actions/getUsers';
import { addTask } from '../../actions/addTask';
import { getCustomerNotes } from '../../actions/getCustomerNotes';

import '../../styles/employee-dashboard.scss'
import classnames from 'classnames';

const CustomerPage = () => {
    const [customer, setCustomer] = useState({});
    const getCustomerFromState = useSelector(state => state.getCustomerReducer.customer);
    const [activeTab, setActiveTab] = useState('1');
    const [addEmployeeModalOpen, setAddEmployeeModalOpen] = useState(false);
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        jobTitle: '',
        officeNumber: '',
        cellNumber: '',
        email: ''
    });
    const [addEmployeeErrors, setAddEmployeeErrors] = useState({});
    const [flag, setFlag] = useState(false);
    const [tasks, setTasks] = useState([]);
    const getTasksFromState = useSelector(state => state.getCustomerTasksReducer.tasks);
    const [addTaskModalOpen, setAddTaskModalOpen] = useState(false);
    const [task, setTask] = useState({
        taskDate: '',
        taskTime: '',
        taskDescription: '',
        customerId: '',
        employeeId: ''
    });
    const [employees, setEmployees] = useState([]);
    const employeeArray = [];
    const getUsersFromState = useSelector(state => state.getUsersReducer.users);
    const getAddTaskSuccess = useSelector(state => state.addTaskReducer.success);
    const [addTaskErrors, setAddTaskErrors] = useState({});
    const getAddTaskErrors = useSelector(state => state.addTaskReducer.errors);
    const [notes, setNotes] = useState([]);
    const getNotesFromState = useSelector(state => state.getCustomerNotesReducer.notes);
    const [addNoteModalOpen, setAddNoteModalOpen] = useState(false);
    const [note, setNote] = useState({
        note: '',
        createdAt: '',
        createdBy: ''
    });
    const [editCustomer, setEditCustomer] = useState({
        businessName: '',
        industry: '',
        primaryContact: {
            firstName: '',
            lastName: ''
        },
        billingContact: {
            firstName: '',
            lastName: ''
        },
        primaryOfficeNumber: '',
        primaryExt: '',
        primaryCellNumber: '',
        primaryEmail: '',
        primaryJobTitle: '',
        billingOfficeNumber: '',
        billingExt: '',
        billingCellNumber: '',
        billingEmail: '',
        billingJobTitle: '',
        shopAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        billingAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        noBillingContact: false
    });
    const [editCustomerModalOpen, setEditCustomerModalOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const customerId = pathArray[pathArray.length - 1];
        dispatch(getCustomer(customerId));
        dispatch(getCustomerTasks(customerId));
        dispatch(getUsers());
        dispatch(getCustomerNotes(customerId));
    }, []);

    useEffect(() => {
        if(getCustomerFromState) {
            setCustomer(getCustomerFromState);
            setTask({
                ...task,
                customerId: getCustomerFromState.userId
            })
        };
    }, [getCustomerFromState]);

    useEffect(() => {
        if(getTasksFromState) {
            setTasks(getTasksFromState);
        };
    }, [getTasksFromState]);

    useEffect(() => {
        if(getNotesFromState) {
            console.log(getNotesFromState)
            setNotes(getNotesFromState);
        };
    }, [getNotesFromState]);

    useEffect(() => {
        if(flag) {
            setFlag(false);
            dispatch(addEmployeeToCustomer(customer));
            toggleAddEmployeeModal();
        };
    }, [flag]);

    useEffect(() => {
        if(getUsersFromState.users) {
            getUsersFromState.users.map(user => {
                if(user.userType === 'employee' && user.active === true) {
                    employeeArray.push({
                        label: `${user.firstName} ${user.lastName}`,
                        value: user.userId,
                        name: 'employeeId',
                        jobTitle: user.jobTitle
                    });
                };
            });

            setEmployees(employeeArray);
        };
    }, [getUsersFromState]);

    useEffect(() => {
        if(getAddTaskSuccess) {
            console.log(getAddTaskSuccess)
            toggleAddTaskModal();
        };
    }, [getAddTaskSuccess]);

    useEffect(() => {
        if(getAddTaskErrors) {
            setAddTaskErrors(getAddTaskErrors);
        };
    }, [getAddTaskErrors]);

    const dateConverter = date => {
        const dateArray = date.split('-');
        return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
    };

    const timeConverter = time => {
        const timeArray = time.split(':');
        const hour = parseInt(timeArray[0]);
        let m;

        if(hour <= 11) {
            m = 'AM';
        } else {
            m = 'PM';
        };

        if(hour === 0) {
            const newHour = '12';
            timeArray[0] = newHour;
        } else if(hour <= 12) {
            const newHour = hour.toString();
            timeArray[0] = newHour;
        } else {
            const newHour = (hour - 12).toString();
            timeArray[0] = newHour;
        };

        return `${timeArray[0]}:${timeArray[1]} ${m}`;
    };

    const employeeFinder = employeeId => {
        employees.forEach(employee => {
            if(employee.value === employeeId) {
                return employee.label;
            };
        });
    };

    const toggleTab = tab => {
        if(activeTab !== tab) {
            setActiveTab(tab);
        };
    };

    const toggleAddEmployeeModal = () => {
        if(addEmployeeModalOpen) {
            setEmployee({
                firstName: '',
                lastName: '',
                jobTitle: '',
                officeNumber: '',
                cellNumber: '',
                email: ''
            });
            setAddEmployeeErrors({});
        };
        setAddEmployeeModalOpen(!addEmployeeModalOpen);
    };

    const toggleAddTaskModal = () => {
        if(addTaskModalOpen) {
            setTask({
                ...task,
                taskDate: '',
                taskTime: '',
                taskDescription: '',
                employeeId: ''
            });
            setAddTaskErrors({});
        };
        setAddTaskModalOpen(!addTaskModalOpen);
    };

    const toggleAddNoteModal = () => {
        if(addNoteModalOpen) {
            setNote({
                note: '',
                createdAt: '',
                createdBy: ''
            });
        };
        setAddNoteModalOpen(!addNoteModalOpen);
    };

    const toggleEditCustomerModal = () => {
        if(editCustomerModalOpen) {
            setEditCustomer({
                businessName: '',
                industry: '',
                primaryContact: {
                    firstName: '',
                    lastName: ''
                },
                billingContact: {
                    firstName: '',
                    lastName: ''
                },
                primaryOfficeNumber: '',
                primaryExt: '',
                primaryCellNumber: '',
                primaryEmail: '',
                primaryJobTitle: '',
                billingOfficeNumber: '',
                billingExt: '',
                billingCellNumber: '',
                billingEmail: '',
                billingJobTitle: '',
                shopAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                billingAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                noBillingContact: false
            });
        };
        setEditCustomerModalOpen(!editCustomerModalOpen);
    };

    const handleAddEmployeeChange = e => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    };

    const handleAddTaskChange = e => {
        setTask({
            ...task,
            [e.target.name]: e.target.value
        });
    };

    const handleAddTaskSelectChange = e => {
        setTask({
            ...task,
            [e.name]: e.value
        });
    };

    const handleAddNoteChange = e => {
        setNote({
            ...note,
            [e.target.name]: e.target.value
        });
    };

    const handleAddEmployeeSubmit = e => {
        e.preventDefault();

        let errors = {};

        if(employee.firstName === '') {
            errors.firstName = 'Must not be empty';
        };
        if(employee.lastName === '') {
            errors.lastName = 'Must not be empty';
        };
        if(employee.jobTitle === '') {
            errors.jobTitle = 'Must not be empty';
        };
        if(employee.officeNumber === '' && employee.cellNumber === '') {
            errors.officeNumber = 'Must not be empty';
            errors.cellNumber = 'Must not be empty'
        } else if(employee.officeNumber !== '' && !employee.officeNumber.match(/^[0-9]{3}[-]{1}[0-9]{3}[-]{1}[0-9]{4}$/)) {
            errors.officeNumber = 'Must be a valid phone number';
        } else if(employee.cellNumber !== '' && !employee.cellNumber.match(/^[0-9]{3}[-]{1}[0-9]{3}[-]{1}[0-9]{4}$/)) {
            errors.cellNumber = 'Must be a valid phone number';
        };
        if(employee.email === '') {
            errors.email = 'Must not be empty';
        } else if(!employee.email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
            errors.email = 'Must be a valid email address';
        };

        setAddEmployeeErrors(errors);

        if(Object.keys(errors).length === 0) {
            if(customer.employees.length === 0) {
                setFlag(true);
                setCustomer({
                    ...customer,
                    employees: [employee]
                });
            } else {
                setFlag(true);
                setCustomer({
                    ...customer,
                    employees: [...customer.employees, employee]
                });
            };
        };
    };

    const handleAddTaskSubmit = e => {
        e.preventDefault();
