import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { Button, ButtonGroup } from 'reactstrap';
import '../../styles/sidebar.scss';

const TaskToggle = () => {
    const [calendar, setCalendarSelected] = useState(false);
    const [hidden, setHidden] = useState(false);
    const location = useLocation();

    useEffect(location => {
        if(location.pathname === '/e_dashboard') {
            setHidden(!hidden);
        }
    }, location)

    return (
        <div className="task-btns">
            <ButtonGroup className="button-group">
                <Button onClick={() => setCalendarSelected(false)} active={calendar === false}>List</Button>
                <Button onClick={() => setCalendarSelected(true)} active={calendar === true}>Calendar</Button>
            </ButtonGroup>
        </div>
    )
}

export default TaskToggle;
