import React, { useState } from "react";
import Select from "react-select";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  FormFeedback,
} from "reactstrap";

import "../../styles/sidebar.scss";

const AddEventBtn = () => {
  const [event, setEvent] = useState({
    title: "",
    startDate: "",
    endDate: "",
    startTime: "",
    endTime: "",
    allDay: false,
    recurring: false,
    daysOfWeek: [],
    extendedProps: {
      location: "",
      invitees: [],
      description: "",
    },
  });
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    if (isOpen) {
      setEvent({
        title: "",
        startDate: "",
        endDate: "",
        startTime: "",
        endTime: "",
        allDay: false,
        recurring: false,
        daysOfWeek: [],
        extendedProps: {
          location: "",
          invitees: [],
          description: "",
        },
      });
    }

    setIsOpen(!isOpen);
  };

  const handleChange = (e) => {
    setEvent({
      ...event,
      [e.target.name]: e.target.value,
    });
  };

  const handleExtendedChange = (e) => {};

  const handleRecurringChange = (e) => {
    setEvent({
      ...event,
      recurring: !event.recurring,
    });
  };

  const handleDaysChange = (e) => {
    let array = event.daysOfWeek;
    let temp = [];

    const day = parseInt(e.target.value);

    if (array.includes(day)) {
      temp = array.filter((i) => i !== day);
      array = temp;
    } else {
      array.push(day);
    }

    array.sort();
    setEvent({
      ...event,
      daysOfWeek: array,
    });
    console.log(array);
  };

  const handleSelectChange = (e) => {

  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="add-event-div">
      <Button color="danger" onClick={toggle}>
        Add Event
      </Button>

      <Modal isOpen={isOpen} toggle={toggle}>
        <ModalHeader toggle={toggle}>Add Event</ModalHeader>
        <ModalBody>
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Title</Label>
              <Input
                type="text"
                name="title"
                onChange={handleChange}
                // invalid={errors.title ? true : false}
              />
              {/* <FormFeedback>{errors.title}</FormFeedback> */}
            </FormGroup>
            <FormGroup>
              <Label>Location</Label>
              <Input
                type="text"
                name="location"
                onChange={handleExtendedChange}
                // invalid={errors.extendedProps && errors.extendedProps.location ? true : false}
              />
              {/* <FormFeedback>{errors.extendedProps && errors.extendedProps.location}</FormFeedback> */}
            </FormGroup>
            <div className="start-end-date-div">
              <FormGroup>
                <Label>Start Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="startDate"
                  onChange={handleChange}
                  // invalid={errors.startDate ? true : false}
                />
                {/* <FormFeedback>{errors.startDate}</FormFeedback> */}
              </FormGroup>
              <FormGroup>
                <Label>End Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="endDate"
                  onChange={handleChange}
                  // invalid={errors.endDate ? true : false}
                />
                {/* <FormFeedback>{errors.endDate}</FormFeedback> */}
              </FormGroup>
            </div>
            <div className="start-end-time-div">
              <FormGroup>
                <Label>Start Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="startTime"
                  onChange={handleChange}
                />
              </FormGroup>
              <FormGroup>
                <Label>End Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="endTime"
                  onChange={handleChange}
                  // invalid={errors.endTime ? true : false}
                />
                {/* <FormFeedback>{errors.endTime}</FormFeedback> */}
              </FormGroup>
            </div>
            <FormGroup>
              <Label>Description</Label>
              <Input
                type="textarea"
                name="description"
                onChange={handleExtendedChange}
              />
            </FormGroup>
            <FormGroup check>
              <Label check>
                Recurring{" "}
                <Input
                  type="checkbox"
                  name="recurring"
                  onChange={handleRecurringChange}
                />
              </Label>
            </FormGroup>
            {event.recurring ? (
              <div>
                <Label>Which days should event recur?</Label>
                <FormGroup check>
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="0"
                      onChange={handleDaysChange}
                    />{" "}
                    Sunday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="1"
                      onChange={handleDaysChange}
                    />{" "}
                    Monday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="2"
                      onChange={handleDaysChange}
                    />{" "}
                    Tuesday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="3"
                      onChange={handleDaysChange}
                    />{" "}
                    Wednesday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="4"
                      onChange={handleDaysChange}
                    />{" "}
                    Thursday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="5"
                      onChange={handleDaysChange}
                    />{" "}
                    Friday
                  </Label>
                  <br />
                  <Label check>
                    <Input
                      type="checkbox"
                      name="daysOfWeek"
                      value="6"
                      onChange={handleDaysChange}
                    />{" "}
                    Saturday
                  </Label>
                </FormGroup>
              </div>
            ) : null}
            <FormGroup>
              <Label>Invitees</Label>
              <Select onChange={handleSelectChange} />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="success">Submit</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default AddEventBtn;
