import { LOGIN_SUCCESS } from '../actions';

const initialState = {
    user: {}
};

export const persistUserReducer = (state = initialState, action) => {
    switch(action.type) {
        case LOGIN_SUCCESS:
            return {
                ...state,
                user: {
                    firstName: action.payload.response.firstName,
                    lastName: action.payload.response.lastName,
                    userType: action.payload.response.userType,
                    tierLevel: action.payload.response.tierLevel
                }
            };
            default: return state;
    };
};
