import React, { useState, useEffect } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback
} from 'reactstrap';
import Select from 'react-select';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddTaskBtn = () => {
    const [hidden, setHidden] = useState(false);
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard' || location.pathname === '/e_dashboard/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className="add-task-div">
            {hidden ? <Button color="success">Add Task</Button> : null}
        </div>
    );
};

export default AddTaskBtn;
