import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Form, Input, Button, Label } from 'reactstrap';
import { login } from '../../actions/login';
import NavBar from './NavBar';
import Footer from './Footer';

import 'bootstrap/dist/css/bootstrap.css';
import '../../styles/login.scss';

const Login = () => {
    const [creds, setCreds] = useState({username: '', password: ''});
    const [error, setError] = useState(false);
    const userType = useSelector(state => state.userType);
    const loggingIn = useSelector(state => state.loggingIn);
    const success = useSelector(state => state.success);
    const stateError = useSelector(state => state.error);
    const dispatch = useDispatch()

    const handleChange = e => {
        setCreds({...creds, [e.target.name]: e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault();

        dispatch(login(creds))
        .then(() => {
            if(localStorage.getItem('token')) {
                return userType === "customer" ?
                    this.props.history.push('/customer') :
                    this.props.history.push('/employee')
            } else {
                setError(true);
            }
        })
        .catch(err => console.log(err));
    }

    return (
        <div className="body">
            <NavBar className="navbar" />

            <div className="login-page">
                <div className="input-box">
                    <h2>Log In</h2>
                    <Form onSubmit={ handleSubmit } className="form-field">
                        <div className="input-field">
                            <Label className="input-label" for="username">Username</Label>
                            <Input
                                className="input"
                                type="text"
                                value={ creds.username }
                                id="username"
                                name="username"
                                onChange={ handleChange }
                            />
                        </div>
                        <div className="input-field">
                            <Label className="input-label" for="password">Password</Label>
                            <Input
                                className="input"
                                type="password"
                                value={ creds.password }
                                name="password"
                                onChange={ handleChange }
                            />
                        </div>

                        <Button type="submit" size="lg" outline color="success" className="btn-centered">Log In</Button>
                        {loggingIn ? <p className="login-status">...Logging In...</p> :
                            success ? <p className="login-status success">Login Successful!</p> :
                            stateError ? <p className="login-status error">Login Failed</p> : null}
                    </Form>
                </div>
            </div>

            <Footer />
        </div>
    )
}

export default Login;
