import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    CardText,
    Table
} from 'reactstrap';

import '../../styles/employee-dashboard.scss';

const OpenWorkOrders = () => {
    const [workOrders, setWorkOrders] = useState([]);
    const getWorkOrders = useSelector(state => state.getWorkOrdersReducer.workOrders);

    useEffect(() => {
        setWorkOrders(getWorkOrders.workOrders);
    }, [getWorkOrders]);

    console.log(workOrders)

    return (
        <div>
            <h1>Open Work Orders</h1>
            {workOrders ? 
                workOrders.map(workOrder => (
                    <Card>
                        <CardHeader className="card-info">
                            <CardText>Job #</CardText>
                            <CardText>Customer Name</CardText>
                            <CardText>Service Type</CardText>
                            <CardText>Service Date</CardText>
                        </CardHeader>
                        <CardBody className="card-info">
                            <CardText>{workOrder.jobNumber}</CardText>
                            <CardText>{workOrder.customerName}</CardText>
                            <CardText>{workOrder.serviceType}</CardText>
                            <CardText>{workOrder.serviceDate}</CardText>
                        </CardBody>
                    </Card>
                ))
            : null}
        </div>
    );
};

export default OpenWorkOrders;
