import React, { useState, useEffect } from 'react';
import { 
    Button, 
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormText,
    FormGroup,
    Label,
    Input, 
    InputGroup 
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddEmployeeBtn = () => {
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/employees' || location.pathname === '/e_dashboard/employees/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        setIsOpen(!isOpen);
    };

    const closeBtn = <Button className="close" onClick={toggle}>&times;</Button>;

    return (
        <div className="add-employee-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Employee</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle} close={closeBtn}>Add Employee</ModalHeader>
                <ModalBody>
                   <Form>
                       <div className="name-div">
                           <FormGroup>
                               <Label for="firstName">First Name</Label>
                               <Input type="text" name="firstName" id="firstName" />
                           </FormGroup>
                           <FormGroup>
                               <Label for="lastName">Last Name</Label>
                               <Input type="text" name="lastName" id="lastName" />
                           </FormGroup>
                       </div>
                       <div className="job-info-div">
                           <FormGroup className="job-title-form-group">
                               <Label for="jobTitle">Job Title</Label>
                               <Input type="select" name="jobTitle" id="jobTitle">
                                   <option>Service Technician</option>
                                   <option>Outside Sales</option>
                                   <option>Service Manager</option>
                                   <option>Dispatch</option>
                                   <option>Office Manager</option>
                                   <option>Owner</option>
                               </Input>
                           </FormGroup>
                           <FormGroup className="tier-level-form-group">
                               <Label for="tierLevel">Tier Level</Label>
                               <Input type="select" name="tierLevel" id="tierLevel">
                                   <option>1</option>
                                   <option>2</option>
                                   <option>3</option>
                               </Input>
                           </FormGroup>
                       </div>
                       <FormGroup>
                           <Label for="phoneNumber">Phone Number</Label>
                           <Input type="text" name="phoneNumber" id="phoneNumber" placeholder="XXX-XXX-XXXX" />
                       </FormGroup>
                       <FormGroup>
                           <Label for="email">Email</Label>
                           <Input type="email" name="email" id="email" placeholder="example@example.com" />
                       </FormGroup>
                       <div className="password-div">
                           <FormGroup>
                               <Label for="password">Password</Label>
                               <Input type="text" name="password" id="password" />
                           </FormGroup>
                           <FormGroup>
                               <Label for="confirmPassword">Confirm Password</Label>
                               <Input type="text" name="confirmPassword" id="confirmPassword" />
                           </FormGroup>
                       </div>
                   </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" type="submit">Submit</Button>
                </ModalFooter>
            </Modal> 
        </div>
    );
};

export default AddEmployeeBtn;
