import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import bootstrapPlugin from '@fullcalendar/bootstrap';
import { getTasks } from '../../actions/getTasks';
import { getWorkOrders } from '../../actions/getWorkOrders';

import '../../styles/employee-dashboard.scss';
import '@fullcalendar/daygrid/main.css';
import '@fullcalendar/timegrid/main.css';
import '../../styles/calendar.scss';

const TaskCalendar = () => {
    const [events, setEvents] = useState([]);
    const [workOrders, setWorkOrders] = useState([]);
    const fetchWorkOrders = useSelector(state => state.getWorkOrdersReducer.workOrders);
    const [tasks, setTasks] = useState([]);
    const fetchTasks = useSelector(state => state.getTasksReducer.tasks);



    return (
        <div className="calendar-page">
            <h1>Calendar</h1>
            <FullCalendar
                plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin, bootstrapPlugin]}
                initialView="dayGridMonth"
                headerToolbar={{
                    left: 'prev,next today',
                    center: 'title',
                    right: 'dayGridMonth,timeGridWeek,timeGridDay'
                }}
                themeSystem="bootstrap"
                height="auto"
            />
        </div>

    );
};

export default TaskCalendar;
