import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import CurrentEmployeesContent from './CurrentEmployeesContent';
import AllEmployeesContent from './AllEmployeesContent';
import { getEmployees } from '../../actions/getEmployees';

import '../../styles/employee-dashboard.scss';

const EmployeesContent = () => {
    const all = useSelector(state => state.employeesToggleReducer.all);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getEmployees());
    }, [dispatch]);

    return (
        <div className="content-page">
            {all ? <AllEmployeesContent /> : <CurrentEmployeesContent />}
        </div>
    );
};

export default EmployeesContent;
