import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddWorkOrderBtn = () => {
    const [workOrder, setWorkOrder] = useState({
        jobNumber: undefined,
        customer: '',
        serviceDate: '',
        serviceType: '',
        prevJobNumber: undefined,
        quote: '',
        notes: '',
        officeNotes: ''
    });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state);
    const getSuccess = useSelector(state => state);
    const [employees, setEmployees] = useState([]);
    const [customers, setCustomers] = useState([]);
    const getUsers = useSelector(state => state.getUsersReducer.users); 
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    useEffect(() => {
        if(getUsers) {
            getUsers.forEach(user => {
                console.log(user.userId);
            })
        }
    })

    const toggle = e => {
        setIsOpen(!isOpen);
        setErrors({});
    };

    const handleSubmit = e => {
        e.preventDefault();
        return;
    };

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className={hidden ? "add-work-order-div": ""}>
            {hidden ? <Button color="success" onClick={toggle}>Create Work Order</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Create Work Order</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label>Customer</Label>

                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
};

export default AddWorkOrderBtn;
