import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  toggleCurrentEmployees,
  toggleAllEmployees,
} from "../../actions/employeesToggle";
import { useLocation } from "react-router-dom";
import { Button, ButtonGroup } from "reactstrap";

import "../../styles/sidebar.scss";

const EmployeesToggle = () => {
  const [hidden, setHidden] = useState(true);
  const all = useSelector((state) => state.employeesToggleReducer.all);
  const dispatch = useDispatch();
  const location = useLocation();

  useEffect(() => {
    changeHidden(location);
  }, [location]);

  const changeHidden = (location) => {
    if (
      location.pathname === "/dashboard/employees" ||
      location.pathname === "/dashboard/employees/"
    ) {
      setHidden(false);
    } else {
      setHidden(true);
    }
  };

  return (
    <div className={hidden ? "" : "task-btns"}>
      {hidden ? null : (
        <ButtonGroup className="button-group">
          <Button
            onClick={() => dispatch(toggleCurrentEmployees())}
            active={all === false}
          >
            Current
          </Button>
          <Button
            onClick={() => dispatch(toggleAllEmployees())}
            active={all === true}
          >
            All
          </Button>
        </ButtonGroup>
      )}
    </div>
  );
};

export default EmployeesToggle;
