import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input
} from 'reactstrap';
import Select from 'react-select';
import { useLocation } from 'react-router-dom';
import Globalize from 'globalize';
import globalizeLocalizer from 'react-widgets-globalize';
import { DateTimePicker } from 'react-widgets';

import '../../styles/sidebar.scss';
import 'react-widgets/lib/scss/react-widgets.scss';

const AddWorkOrderBtn = () => {
    const [workOrder, setWorkOrder] = useState({
        jobNumber: undefined,
        customer: '',
        serviceDate: '',
        serviceType: '',
        prevJobNumber: undefined,
        quote: '',
        notes: '',
        officeNotes: ''
    });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state);
    const getSuccess = useSelector(state => state);
    const [employees, setEmployees] = useState([]);
    const employeesArray = [];
    const [customers, setCustomers] = useState([]);
    const customersArray = [];
    const getUsers = useSelector(state => state.getUsersReducer.users); 
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    Globalize.locale('en');
    globalizeLocalizer();
    let monthYearFormatter = Globalize.dateFormatter({ raw: 'mmm YY' });
    let monthYearParser = Globalize.dateParser({ raw: 'mmm YY' });

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    useEffect(() => {
        if(getUsers.users) {
            getUsers.users.forEach(user => {
                if(user.userType === 'employee') {
                    employeesArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId
                        }
                    );
                } else {
                    customersArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId
                        }
                    );
                };
            });
        };
        setEmployees(employeesArray);
        setCustomers(customersArray);
    }, [getUsers]);

    const toggle = e => {
        setIsOpen(!isOpen);
        setErrors({});
    };

    const handleSubmit = e => {
        e.preventDefault();
        return;
    };

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className={hidden ? "add-work-order-div": ""}>
            {hidden ? <Button color="success" onClick={toggle}>Create Work Order</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Create Work Order</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label>Customer</Label>
                            <Select options={customers} />
                        </FormGroup>
                        <FormGroup>
                            <Label>Date</Label>
                            <DateTimePicker format={monthYearFormatter} parse={monthYearParser} />
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
};

export default AddWorkOrderBtn;
