import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import {
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback,
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Table
} from 'reactstrap';
import SignatureCanvas from 'react-signature-canvas';
import EmployeeNavbar from './EmployeeNavbar';
import EmployeeSidebar from './EmployeeSidebar';
import Footer from './Footer';
import { getWorkOrder } from '../../actions/getWorkOrder';
import { workOrderReviewSubmit } from '../../actions/workOrderReviewSubmit';

import '../../styles/employee-dashboard.scss';

const WorkOrderPage = () => {
    const [workOrder, setWorkOrder] = useState({});
    const [customer, setCustomer] = useState({});
    const [mapsAddress, setMapsAddress] = useState('');
    const [employees, setEmployees] = useState([]);
    const getWorkOrderFromState = useSelector(state => state.getWorkOrderReducer.workOrder);
    const getUsers = useSelector(state => state.getWorkOrderReducer.users);
    const [isOpenServiceModal, setIsOpenServiceModal] = useState(false);
    const [service, setService] = useState({
        make: '',
        type: '',
        model: '',
        serialNumber: '',
        bayNumber: '',
        serviceDone: '',
        partsUsed: [],
        partsNeeded: []
    });
    const [serviceErrors, setServiceErrors] = useState({});
    const [partUsed, setPartUsed] = useState({
        quantity: '',
        partDescription: '',
        partNumber: '',
        images: []
    });
    const [partUsedErrors, setPartUsedErrors] = useState({});
    const [partNeeded, setPartNeeded] = useState({
        quantity: '',
        partDescription: '',
        partNumber: '',
        images: []
    });
    const [partNeededErrors, setPartNeededErrors] = useState({});
    const [isOpenPartUsed, setIsOpenPartUsed] = useState(false);
    const [isOpenPartNeeded, setIsOpenPartNeeded] = useState(false);
    const [startTime, setStartTime] = useState(0);
    const [endTime, setEndTime] = useState(0);
    const [hideClockIn, setHideClockIn] = useState(false);
    const [hideOfficeNotes, setHideOfficeNotes] = useState(false);
    const [hideSignaturePad, setHideSignaturePad] = useState(true);
    const [hideSubmitForSignatureButton, setHideSubmitForSignatureButton] = useState(false);
    const [hideSubmitForReviewButton, setHideSubmitForReviewButton] = useState(true);
    let sigPad = {};
    const [flag, setFlag] = useState(false);
    const [waitingForReview, setWaitingForReview] = useState(false);
    const success = useSelector(state => state.workOrderReviewSubmitReducer.success);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const jobId = pathArray[pathArray.length - 1];
        dispatch(getWorkOrder(jobId));
    }, []);

    useEffect(() => {
        if(getWorkOrderFromState) {
            setWorkOrder(getWorkOrderFromState);
        };
    }, [getWorkOrderFromState]);

    useEffect(() => {
        let crewMembers = [];

        if(getUsers) {
            getUsers.forEach(user => {
                if(user.userType === 'customer') {
                    setCustomer(user);
                } else {
                    crewMembers.push(user);
                };
            });
            setEmployees(crewMembers);
        }
    }, [getUsers]);

    useEffect(() => {
        if(customer) {
            addressConverter(customer);
        };
    }, [customer]);

    useEffect(() => {
        if(startTime !== 0 && endTime !== 0) {
            jobTimeConverter();
        };
    }, [endTime]);

    useEffect(() => {
        if(flag) {
            dispatch(workOrderReviewSubmit(workOrder));
            setHideSubmitForReviewButton(true);
        };
    }, [flag]);

    useEffect(() => {
        if(success) {
            setWaitingForReview(true);
        };
    }, [success]);

    const typeConverter = types => {
        if(types) {
            if(types.length === 1) {
                return types;
            } else {
                return types.join(', ');
            };
        };
    };

    const dateConverter = date => {
        if(date) {
            const dateArray = date.split('-');
            return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
        };
    };

    const timeConverter = time => {
        if(time) {
            const timeArray = time.split(':');
            const hour = parseInt(timeArray[0]);
            let m;

            if(hour <= 11) {
                m = 'AM';
            } else {
                m = 'PM';
            };

            if(hour <= 12) {
                const newHour = hour.toString();
                timeArray[0] = newHour;
            } else {
                const newHour = (hour - 12).toString();
                timeArray[0] = newHour;
            };

            return `${timeArray[0]}:${timeArray[1]} ${m}`;
        };
    };

    const addressConverter = customer => {
        const address = customer.address;
        const city = customer.city;
        const state = customer.state;
        const zipcode = customer.zipcode;

        let newAddress = ''
        let newCity = '';

        if(address) {
            newAddress = address.split(' ').join('+');
        };
        if(city) {
            newCity = city.split(' ').join('+');
        }

        const searchableAddress = `${newAddress},+${newCity},+${state}+${zipcode}`;
        setMapsAddress(searchableAddress);
    };

    const crewConverter = members => {
        let crewNames = [];

        if(members) {
            members.forEach(member => {
                crewNames.push(`${member.firstName} ${member.lastName}`);
            });

            if(crewNames.length === 1) {
                return crewNames;
            } else {
                return crewNames.join(', ');
            };
        };
    };

    const clockIn = () => {
        const date = new Date();
        const time = date.getTime();

        setStartTime(time);
    };

    const clockOut = () => {
        if(startTime === 0) {
            alert('Please clock in first');
        } else {
            const date = new Date();
            const time = date.getTime();

            setEndTime(time);
        };
    };

    const jobTimeConverter = () => {
        if(startTime && endTime) {
          const totalTime = endTime - startTime;

          const seconds = Math.floor((totalTime / 1000) % 60);
          const minutes = Math.floor((totalTime / (1000 * 60)) % 60);
          const hours = Math.floor((totalTime / (1000 * 60 * 60)) % 24);

          const trueHours = hours < 10 ? "0" + hours : hours;
          const trueMinutes = minutes < 10 ? "0" + minutes : minutes;
          const trueSeconds = seconds < 10 ? "0" + seconds : seconds;

          const timeAtJob = trueHours + ':' + trueMinutes + ':' + trueSeconds;

          console.log(timeAtJob)

          setWorkOrder({
              ...workOrder,
              timeAtJob: timeAtJob
          });
          setHideClockIn(true);
        };
    };

    const toggleServiceModal = () => {
        setIsOpenServiceModal(!isOpenServiceModal);
    };

    const handleChangeService = e => {
        setService({
            ...service,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmitService = e => {
        e.preventDefault();
        const errors = {};

        if(service.make.trim() === '') {
            errors.make = 'Must not be empty';
        };
        if(service.type.trim() === '') {
            errors.type = 'Must not be empty';
        };
        if(service.model.trim() === '') {
            errors.model = 'Must not be empty';
        };
        if(service.serialNumber.trim() === '') {
            errors.serialNumber = 'Must not be empty';
        };
        if(service.serviceDone.trim() === '') {
            errors.serviceDone = 'Must not be empty';
        };

        if(Object.keys(errors).length !== 0) {
            setServiceErrors(errors);
        } else {
            if(!workOrder.serviceDone) {
                setWorkOrder({
                    ...workOrder,
                    serviceDone: [service]
                });
            } else {
                setWorkOrder({
                    ...workOrder,
                    serviceDone: [...workOrder.serviceDone, service]
                });
            };

            setService({
                make: '',
                type: '',
                model: '',
                serialNumber: '',
                bayNumber: '',
                serviceDone: '',
                partsUsed: [],
                partsNeeded: []
            });

            setIsOpenServiceModal(false);
            setServiceErrors({});
        };
    };

    const togglePartUsed = () => {
        setIsOpenPartUsed(!isOpenPartUsed);
    };

    const togglePartNeeded = () => {
        setIsOpenPartNeeded(!isOpenPartNeeded);
    };

    const handleChangePartUsed = e => {
        setPartUsed({
            ...partUsed,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmitPartUsed = e => {
        e.preventDefault();
        const errors = {};

        if(partUsed.quantity.trim() === '') {
            errors.quantity = 'Must not be empty';
        };
        if(partUsed.partDescription.trim() === '') {
            errors.partDescription = 'Must not be empty';
        };
        if(partUsed.partNumber.trim() === '') {
            errors.partNumber = 'Must not be empty';
        };

        if(Object.keys(errors).length !== 0) {
            setPartUsedErrors(errors);
        } else {
            setService({
                ...service,
                partsUsed: [...service.partsUsed, partUsed]
            });

            setPartUsed({
                quantity: '',
                partDescription: '',
                partNumber: '',
                images: []
            });

            setIsOpenPartUsed(false);
            setPartUsedErrors({});
        };
    };

    const handleChangePartNeeded = e => {
        setPartNeeded({
            ...partNeeded,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmitPartNeeded = e => {
        e.preventDefault();
        const errors = {};

        if(partNeeded.quantity.trim() === '') {
            errors.quantity = 'Must not be empty';
        };
        if(partNeeded.partDescription.trim() === '') {
            errors.partDescription = 'Must not be empty';
        };
        if(partNeeded.partNumber.trim() === '') {
            errors.partNumber = 'Must not be empty';
        };

        if(Object.keys(errors).length !== 0) {
            setPartNeededErrors(errors);
        } else {
            setService({
                ...service,
                partsNeeded: [...service.partsNeeded, partNeeded]
            });

            setPartNeeded({
                quantity: '',
                partDescription: '',
                partNumber: '',
                images: []
            });

            setIsOpenPartNeeded(false);
            setPartNeededErrors({});
        };
    };

    const handleSubmitForSignature = e => {
        e.preventDefault();
        setHideOfficeNotes(true);
        setHideSignaturePad(false);
        setHideSubmitForSignatureButton(true);
        setHideSubmitForReviewButton(false);
    };

    const clearSignaturePad = e => {
        sigPad.clear();
    };

    const handleSubmitForReview = e => {
        e.preventDefault();
        setWorkOrder({
            ...workOrder,
            signature: sigPad.getTrimmedCanvas().toDataURL('image/png')
        });
        setFlag(true);
    };

    return (
        <div>
            <EmployeeNavbar />
            <div className="work-order-page">
                <EmployeeSidebar />
                {workOrder ? <div className="work-order-page-content">
                    <h1>{`Work Order #${workOrder.jobNumber}`}</h1>
                    <Form className="work-order-info">
                        <FormGroup>
                            <Label for="customerName">Customer Name</Label>
                            <Input disabled value={`${customer.firstName} ${customer.lastName}`} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="customerAddress">Customer Address</Label>
                            <a href={`http://maps.google.com/maps?q=${mapsAddress}`}>
                                <Input disabled value={`${customer.address}, ${customer.city}, ${customer.state} ${customer.zipcode}`} />
                            </a>
                        </FormGroup>        
                        <FormGroup className="type-input">
                            <Label for="serviceType">Service Type(s)</Label>
                            <Input disabled value={typeConverter(workOrder.serviceType)} />
                        </FormGroup>
                        <div className="date-div">
                            <FormGroup>
                                <Label for="serviceDate">Service Date</Label>
                                <Input disabled value={dateConverter(workOrder.serviceDate)} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="serviceTime">Service Time</Label>
                                <Input disabled value={timeConverter(workOrder.serviceTime)} />
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="crewMembers">Crew Members</Label>
                            <Input disabled value={crewConverter(employees)} />
                        </FormGroup>
                        <FormGroup className="description-input">
                            <Label for="serviceDescription">Service Description</Label>
                            <Input disabled type="textarea" value={workOrder.serviceDescription} />
                        </FormGroup>
                        {hideClockIn ? 
                        <FormGroup>
                            <Label for="timeOnJob">Time At Job</Label>
                            <Input disabled type="text" value={workOrder.timeAtJob ? workOrder.timeAtJob : null} />
                        </FormGroup>
                        : null}
                        <div className="button-div">
                            {!hideClockIn ? 
                            <div className="secondary-button-div">
                                <FormGroup>
                                    {startTime === 0 ?
                                    <Button color="primary" onClick={clockIn}>Clock In</Button>
                                    :
                                    <Button disabled color="primary">Clock In</Button>}
                                </FormGroup>
                                <FormGroup>
                                    {endTime === 0 ?
                                    <Button color="primary" onClick={clockOut}>Clock Out</Button>
                                    :
                                    <Button disabled color="primary">Clock Out</Button>}
                                </FormGroup>
                            </div> : null}
                            {workOrder.serviceDone ? workOrder.serviceDone.length > 0 ?
                            <div className="service-table">
                                <h5>Services</h5>
                                <Table hover size="sm">
                                    <thead>
                                        <th>Service Type</th>
                                        <th>Service Description</th>
                                        <th>Parts Needed</th>
                                    </thead>
                                    <tbody>
                                    {workOrder.serviceDone.map(service => (
                                        <tr>
                                            <td scope="row">{service.type}</td>
                                            <td>{service.serviceDone}</td>
                                            <td>{service.partsNeeded.length > 0 ? 'Y' : 'N'}</td>
                                        </tr>
                                    ))}
                                    </tbody>
                                </Table>
                            </div>
                            : null : null}
                            <FormGroup>
                                <Button outline color="success" onClick={toggleServiceModal}>Add Service</Button>
                            </FormGroup>
                            <div className="secondary-button-div">
                                <FormGroup>
                                    <Button outline color="info">Add Inspection</Button>
                                </FormGroup>
                                <FormGroup>
                                    <Button outline color="warning" className="training-button">Add Training</Button>
                                </FormGroup>
                            </div>
                        </div>
                        <div className="travel-div">
                            <FormGroup>
                                <Label for="travelTime">Travel Time (Roundtrip)</Label>
                                <Input type="text" name="travelTime" />
                            </FormGroup>
                            <FormGroup>
                                <Label for="travelMileage">Travel Mileage (Roundtrip)</Label>
                                <Input type="text" />
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="notes">Notes</Label>
                            <Input type="textarea" value={workOrder.notes ? workOrder.notes : null} />
                        </FormGroup>
                        {!workOrder.signature ? hideOfficeNotes ? null : <FormGroup>
                            <Label for="officeNotes">Office Notes (NOT FOR CUSTOMER)</Label>
                            <Input type="textarea" value={workOrder.officeNotes ? workOrder.officeNotes : null} />
                        </FormGroup> : null}
                        {!workOrder.signature ? hideSignaturePad ? null : <FormGroup>
                            <div className="signature-pad-header">
                                <Label for="signature">Signature</Label>
                                <Button outline color="danger" className="clear-button" onClick={clearSignaturePad} ><i className="far fa-times" /></Button>
                            </div>
                            {!success ? <SignatureCanvas canvasProps={{className: "signature-pad"}} ref={(ref) => { sigPad = ref }} />
                            : <img src={workOrder.signature} alt="Signature" />}
                        </FormGroup> : null}
                        {!workOrder.signature ? hideSubmitForSignatureButton ? null : <FormGroup className="submit-button">
                            <Button color="success" onClick={handleSubmitForSignature}>Submit for Signature</Button>
                        </FormGroup> : null}
                        {!workOrder.signature ? hideSubmitForReviewButton ? null : <FormGroup className="submit-button">
                            <Button color="success" onClick={handleSubmitForReview}>Submit for Review</Button>
                        </FormGroup> : null}
                        {!workOrder.signature ? waitingForReview ? <FormGroup className="submit-button">
                            <Button outline disabled color="danger">Waiting for Review</Button>
                        </FormGroup> : null : null}
                        {workOrder.signature ? <FormGroup>
                            <Label for="signature">Signature</Label>
                            <img src={workOrder.signature} alt="Signature" className="signature-img" />
                        </FormGroup> : null}
                        {workOrder.signature ? <FormGroup className="submit-button">
                            <Button outline disabled color="danger">Waiting for Review</Button>
                        </FormGroup> : null}
                    </Form>
                </div> : null}
                <Modal isOpen={isOpenServiceModal} toggle={toggleServiceModal}>
                    <ModalHeader toggle={toggleServiceModal}>Add Service</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleSubmitService}>
                            <div className="equipment-div">
                                <FormGroup>
                                    <Label for="make">Make</Label>
                                    <Input type="text" name="make" onChange={handleChangeService} invalid={serviceErrors.make ? true : false} />
                                    <FormFeedback>{serviceErrors.make}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="type">Type</Label>
                                    <Input type="text" name="type" onChange={handleChangeService} invalid={serviceErrors.type ? true : false} />
                                    <FormFeedback>{serviceErrors.type}</FormFeedback>
                                </FormGroup>
                            </div>
                            <div className="equipment-div">
                                <FormGroup>
                                    <Label for="model">Model</Label>
                                    <Input type="text" name="model" onChange={handleChangeService} invalid={serviceErrors.model ? true : false} />
                                    <FormFeedback>{serviceErrors.model}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="serialNumber">Serial Number</Label>
                                    <Input type="text" name="serialNumber" onChange={handleChangeService} invalid={serviceErrors.serialNumber ? true : false} />
                                    <FormFeedback>{serviceErrors.serialNumber}</FormFeedback>
                                </FormGroup>
                            </div>
                            <FormGroup>
                                <Label for="bayNumber">Bay Number</Label>
                                <Input type="text" name="bayNumber" onChange={handleChangeService} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="serviceDone">What work was done?</Label>
                                <Input type="textarea" name="serviceDone" onChange={handleChangeService} invalid={serviceErrors.serviceDone ? true : false} />
                                <FormFeedback>{serviceErrors.serviceDone}</FormFeedback>
                            </FormGroup>
                            {service.partsUsed.length > 0 ? 
                            <div className="part-div">
                                <h5>Parts Used</h5>
                                <Table hover size="sm" className="part-table">
                                    <thead>
                                        <th>Quantity</th>
                                        <th>Description</th>
                                        <th>Part Number</th>
                                        <th>Images</th>
                                    </thead>
                                    <tbody>
                                    {service.partsUsed.map(part => (
                                        <tr>
                                            <td scope="row">{part.quantity}</td>
                                            <td>{part.partDescription}</td>
                                            <td>{part.partNumber}</td>
                                            <td>{part.images}</td>
                                        </tr>
                                    ))}
                                    </tbody>
                                </Table>
                            </div>
                            : null}
                            {service.partsNeeded.length > 0 ? 
                            <div className="part-div">
                                <h5>Parts Needed</h5>
                                <Table hover size="sm" className="part-table">
                                    <thead>
                                        <th>Quantity</th>
                                        <th>Description</th>
                                        <th>Part Number</th>
                                        <th>Images</th>
                                    </thead>
                                    <tbody>
                                    {service.partsNeeded.map(part => (
                                        <tr>
                                            <td scope="row">{part.quantity}</td>
                                            <td>{part.partDescription}</td>
                                            <td>{part.partNumber}</td>
                                            <td>{part.images}</td>
                                        </tr>
                                    ))}
                                    </tbody>
                                </Table>
                            </div>
                            : null}
                            <FormGroup className="parts-group">
                                <Button outline color="success" onClick={togglePartUsed}>Add Part Used</Button>
                                <Button outline color="danger" onClick={togglePartNeeded}>Add Part Needed</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleSubmitService}>Submit</Button>
                    </ModalFooter>
                </Modal>
                <Modal isOpen={isOpenPartUsed} toggle={togglePartUsed}>
                    <ModalHeader toggle={togglePartUsed}>Add Part Used</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleSubmitPartUsed}>
                            <FormGroup>
                                <Label>Quantity</Label>
                                <Input type="text" name="quantity" onChange={handleChangePartUsed} invalid={partUsedErrors.quantity ? true : false} />
                                <FormFeedback>{partUsedErrors.quantity}</FormFeedback>                              
                            </FormGroup>
                            <FormGroup>
                                <Label>Part Description</Label>
                                <Input type="text" name="partDescription" onChange={handleChangePartUsed} invalid={partUsedErrors.partDescription ? true : false} />
                                <FormFeedback>{partUsedErrors.partDescription}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label>Part Number</Label>
                                <Input type="text" name="partNumber" onChange={handleChangePartUsed} invalid={partUsedErrors.partNumber ? true : false} />
                                <FormFeedback>{partUsedErrors.partNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label>Picture(s)</Label>
                                <Input multiple type="file" name="images" onChange={handleChangePartUsed} />
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleSubmitPartUsed}>Submit</Button>
                    </ModalFooter>
                </Modal>
                <Modal isOpen={isOpenPartNeeded} toggle={togglePartNeeded}>
                    <ModalHeader toggle={togglePartNeeded}>Add Part Needed</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleSubmitPartNeeded}>
                            <FormGroup>
                                <Label>Quantity</Label>
                                <Input type="text" name="quantity" onChange={handleChangePartNeeded} invalid={partNeededErrors.quantity ? true : false} />
                                <FormFeedback>{partNeededErrors.quantity}</FormFeedback>                               
                            </FormGroup>
                            <FormGroup>
                                <Label>Part Description</Label>
                                <Input type="text" name="partDescription" onChange={handleChangePartNeeded} invalid={partNeededErrors.partDescription ? true : false} />
                                <FormFeedback>{partNeededErrors.partDescription}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label>Part Number</Label>
                                <Input type="text" name="partNumber" onChange={handleChangePartNeeded} invalid={partNeededErrors.partNumber ? true : false} />
                                <FormFeedback>{partNeededErrors.partNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label>Picture(s)</Label>
                                <Input multiple type="file" name="images" onChange={handleChangePartNeeded} />
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleSubmitPartNeeded}>Submit</Button>
                    </ModalFooter>
                </Modal>
            </div>
            <Footer />
        </div>
    );
};

export default WorkOrderPage;
