import {
    ROUTE_START,
    ROUTE_SUCCESS,
    ROUTE_FAILED
} from '../actions';

const initialState = {
    user: null,
    gettingUser: false,
    error: null
};

export const routeToDashboardReducer = (state = initialState, action) => {
    switch(action.type) {
        case ROUTE_START:
            return {
                ...state,
                gettingUser: true
            };
        case ROUTE_SUCCESS:
            return {
                ...state,
                gettingUser: false,
                user: action.payload
            };
        case ROUTE_FAILED:
            return {
                ...state,
                gettingUser: false,
                error: action.payload
            };
        default: return state;
    };
};
