import { dbUrl } from "./index";
import axiosWithAuth from "../contexts/withAuth";

export const ROUTE_START = "ROUTE_START";
export const ROUTE_SUCCESS = "ROUTE_SUCCESS";
export const ROUTE_FAILED = "ROUTE_FAILED";

export const routeToDashboard = (uid) => async (dispatch) => {
  dispatch({ type: ROUTE_START });

  const axiosAuth = await axiosWithAuth();

  return await axiosAuth
    .get(`${dbUrl}/currentUser`, uid)
    .then((res) => {
      dispatch({
        type: ROUTE_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ROUTE_FAILED,
        payload: err,
      });
    });
};
