import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useHistory, Link } from "react-router-dom";
import { Alert, Form, Input, Button, Label } from "reactstrap";
// import { login } from '../../actions/login';
import { useAuth } from "../../contexts/AuthContext";
import LoginNav from "./LoginNav";
import Footer from "../Employees/Footer";

import "bootstrap/dist/css/bootstrap.css";
import "../../styles/login.scss";

const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { login } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const userType = useSelector((state) => state.loginReducer.userType);
  const loggingIn = useSelector((state) => state.loginReducer.loggingIn);
  const success = useSelector((state) => state.loginReducer.success);
  const failure = useSelector((state) => state.loginReducer.error);
  const dispatch = useDispatch();
  const history = useHistory();

  // const handleChange = (e) => {
  //   setCreds({ ...creds, [e.target.name]: e.target.value });
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError("");
      setLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
    } catch {
      setError("Failed to sign in");
    }

    setLoading(false);
  };

  // useEffect(() => {
  //   if (userType) {
  //     return userType === "employee"
  //       ? history.push("/e_dashboard")
  //       : history.push("/c_dashboard");
  //   }
  // }, [userType]);

  return (
    <div className="body">
      <LoginNav className="navbar" />
      <div className="login-page">
        <div className="input-box">
          <h2>Log In</h2>
          {error && <Alert color="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit} className="form-field">
            <div className="input-field">
              <Label className="input-label" for="email">
                Email
              </Label>
              <Input
                className="input"
                type="text"
                ref={emailRef}
                id="email"
                name="email"
              />
            </div>
            <div className="input-field">
              <Label className="input-label" for="password">
                Password
              </Label>
              <Input
                className="input"
                type="password"
                ref={passwordRef}
                name="password"
              />
            </div>

            <Button
              type="submit"
              size="lg"
              outline
              color="success"
              className="btn-centered"
            >
              Log In
            </Button>
            {/* {loggingIn ? (
              <p className="login-status">...Logging In...</p>
            ) : success ? (
              <p className="login-status success">Login Successful!</p>
            ) : failure ? (
              <p className="login-status error">Login Failed</p>
            ) : null} */}
          </Form>
          <div>
            <Link to="">Forgot Password?</Link>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Login;
