import {
    GET_JOB_TITLES_START,
    GET_JOB_TITLES_SUCCESS,
    GET_JOB_TITLES_FAILURE
} from '../actions';

const initialState = {
    jobTitles: [],
    gettingJobTitles: false,
    error: null
};

export const getJobTitlesReducer = (state = initialState, action) => {
    switch(action.type) {
        case GET_JOB_TITLES_START:
            return {
                ...state,
                gettingJobTitles: true
            };
        case GET_JOB_TITLES_SUCCESS:
            return {
                ...state,
                gettingJobTitles: false,
                jobTitles: action.payload
            };
        case GET_JOB_TITLES_FAILURE:
            return {
                ...state,
                gettingJobTitles: false,
                error: action.payload
            };
        default: return state;
    };
};
