import axiosWithAuth from "../contexts/withAuth";
import { dbUrl } from "./index";

export const ROUTE_START = "ROUTE_START";
export const ROUTE_SUCCESS = "ROUTE_SUCCESS";
export const ROUTE_FAILED = "ROUTE_FAILED";

export const routeToDashboard = (user) => async (dispatch) => {
  dispatch({ type: ROUTE_START });

  console.log(user)

  return await axiosWithAuth()
    .get(`${dbUrl}/currentUser`, user)
    .then((res) => {
      dispatch({
        type: ROUTE_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ROUTE_FAILED,
        payload: err,
      });
    });
};
