import React, { useState } from 'react';
import { 
    Table,
    Modal,
    ModalHeader,
    ModalBody,
    Button,
    Form,
    FormGroup,
    Label,
    Input
} from 'reactstrap';

import '../../styles/employee-dashboard.scss';

const EmployeeTable = props => {
    const [isOpenModalOne, setIsOpenModalOne] = useState(false);
    const [isOpenModalTwo, setIsOpenModalTwo] = useState(false);
    const [curEmployee, setCurEmployee] = useState({
        firstName: '',
        lastName: '',
        jobTitle: '',
        tierLevel: null,
        phoneNumber: '',
        email: ''
    });

    const toggleOne = (e, employee) => {
        setIsOpenModalOne(!isOpenModalOne);
        if(isOpenModalOne === true) {
            setCurEmployee({
                firstName: employee.firstName,
                lastName: employee.lastName,
                jobTitle: employee.jobTitle,
                tierLevel: employee.tierLevel,
                phoneNumber: employee.phoneNumber,
                email: employee.email
            });
        } else {
            setCurEmployee({
                firstName: '',
                lastName: '',
                jobTitle: '',
                tierLevel: null,
                phoneNumber: '',
                email: ''
            });
        };
    };

    const toggleTwo = (e, employee) => {
        setIsOpenModalTwo(!isOpenModalTwo);
        if(isOpenModalTwo) {
            setCurEmployee({
                firstName: employee.firstName,
                lastName: employee.lastName,
                jobTitle: employee.jobTitle,
                tierLevel: employee.tierLevel,
                phoneNumber: employee.phoneNumber,
                email: employee.email
            });
        } else {
            setCurEmployee({
                firstName: '',
                lastName: '',
                jobTitle: '',
                tierLevel: null,
                phoneNumber: '',
                email: ''
            });
        };
    };

    const handleChange = e => {
        return;
    };

    const handleSubmit = e => {
        return;
    }

    return (
        <div>
            <Table hover>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Job Title</th>
                        <th>Tier Level</th>
                        <th>Phone Number</th>
                        <th>Email</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {props ? props.props.map(employee => (
                        <tr>
                            <th scope="row">{employee.firstName} {employee.lastName}</th>
                            <td>{employee.jobTitle}</td>
                            <td>{employee.tierLevel}</td>
                            <td>
                                <a href={`tel:+1-${employee.phoneNumber}`}>{employee.phoneNumber}</a>
                            </td>
                            <td>{employee.email}</td>
                            <td onClick={(e) => toggleOne(e, employee)}><i className="fad fa-pen" /></td>
                            <td onClick={employee => toggleTwo(employee)}><i className="fad fa-trash" /></td>
                        </tr>
                    )) : null}
                </tbody>
            </Table>
            <Modal isOpen={isOpenModalOne} toggle={toggleOne}>
                <ModalHeader toggle={toggleOne}>Edit Employee</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <div className="name-div">
                            <FormGroup>
                                <Label for="firstName">First Name</Label>
                                <Input type="text" name="firstName" id="firstName" onChange={handleChange} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="lastName">Last Name</Label>
                                <Input type="text" name="lastName" id="lastName" onChange={handleChange} />
                            </FormGroup>
                        </div>
                        <div className="job-info-div">
                            <FormGroup className="job-title-form-group">
                                <Label for="jobTitle">Job Title</Label>
                                <Input type="select" name="jobTitle" id="jobTitle" onChange={handleChange}>
                                    <option>Select</option>
                                    <option>Service Technician</option>
                                    <option>Outside Sales</option>
                                    <option>Service Manager</option>
                                    <option>Dispatch</option>
                                    <option>Office Manager</option>
                                    <option>Owner</option>
                                </Input>
                            </FormGroup>
                            <FormGroup className="tier-level-form-group">
                                <Label for="tierLevel">Tier Level</Label>
                                <Input type="select" name="tierLevel" id="tierLevel" onChange={handleChange}>
                                    <option>Select</option>
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                </Input>
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="phoneNumber">Phone Number</Label>
                            <Input type="text" name="phoneNumber" id="phoneNumber" placeholder="XXX-XXX-XXXX" onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="email" name="email" id="email" onChange={handleChange}></Input>
                        </FormGroup>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
};

export default EmployeeTable;
