import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const TasksListContent = () => {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const getTasks = useSelector(state => state.getTasksReducer.tasks);

    useEffect(() => {
        setTasks(getTasks.tasks);
        setLoading(false);
        console.log(tasks)
    }, [getTasks]);

    return (
        <div>
        {tasks ?
            tasks.map(task => (
                <div>
                    <h3>{task.title}</h3>
                    <p>{task.body}</p>
                </div>
            )) :
            null
        }
        </div>
    );
};

export default TasksListContent;
