import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  Form,
  FormGroup,
  FormFeedback,
  Label,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Table,
} from "reactstrap";
import { getTasks } from "../../actions/getTasks";
import { getWorkOrders } from "../../actions/getWorkOrders";
import Select from "react-select";

import "../../styles/task.scss";

const Task = () => {
  const [tasks, setTasks] = useState([]);
  const fetchTasks = useSelector((state) => state.getTasksReducer.tasks);
  const [workOrders, setWorkOrders] = useState([]);
  const fetchWorkOrders = useSelector(
    (state) => state.getWorkOrdersReducer.workOrders
  );
  const employees = [];
  const [isOpen, setIsOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState();
  const [errors, setErrors] = useState({
    taskDescription: '',
    taskDate: '',
    taskTime: '',
    employeeId: ''
  });
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getTasks());
    dispatch(getWorkOrders());
  }, []);

  useEffect(() => {
    setTasks(fetchTasks.tasks);
  }, [fetchTasks]);

  useEffect(() => {
    setWorkOrders(fetchWorkOrders.workOrders);
  }, [fetchWorkOrders]);

  const dateConverter = (date) => {
    const dateArray = date.split("-");
    return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
  };

  const timeConverter = (time) => {
    const timeArray = time.split(":");
    const hour = parseInt(timeArray[0]);
    let m;

    if (hour <= 11) {
      m = "AM";
    } else {
      m = "PM";
    }

    if (hour === 0) {
      const newHour = "12";
      timeArray[0] = newHour;
    } else if (hour <= 12) {
      const newHour = hour.toString();
      timeArray[0] = newHour;
    } else {
      const newHour = (hour - 12).toString();
      timeArray[0] = newHour;
    }

    return `${timeArray[0]}:${timeArray[1]} ${m}`;
  };

  const toggle = (e) => {
    setCurrentTask(e.target)
    setIsOpen(!isOpen);
  };

  const handleChange = () => {

  };

  const handleSelectChange = () => {

  };

  const handleSubmit = () => {};

  return (
    <div className="tasks-page">
      <h1>Tasks</h1>
      {tasks
        ? tasks.map((task) => (
            <Card onClick={toggle} className="tasks-page-task-card">
              <Table borderless>
                <thead>
                  <tr>
                    <th>Task</th>
                    <th>Date</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{task.taskDescription}</td>
                    <td>{dateConverter(task.taskDate)}</td>
                    <td>{timeConverter(task.taskTime)}</td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          ))
        : null}
      <Modal isOpen={isOpen} toggle={toggle}>
        <ModalHeader toggle={toggle}>Task</ModalHeader>
        <ModalBody>
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label>Task</Label>
              <Input
                type="textarea"
                name="taskDescription"
                value={currentTask && currentTask.taskDescription}
                onChange={handleChange}
                invalid={errors.taskDescription ? true : false}
              />
              <FormFeedback>{errors.taskDescription}</FormFeedback>
            </FormGroup>
            <div>
              <FormGroup>
                <Label>Date</Label>
                <Input
                  type="date"
                  placeholder="date placeholder"
                  name="taskDate"
                  value={currentTask && currentTask.taskDate}
                  onChange={handleChange}
                  invalid={errors.taskDate ? true : false}
                />
                <FormFeedback>{errors.taskDate}</FormFeedback>
              </FormGroup>
              <FormGroup>
                <Label>Time</Label>
                <Input
                  type="time"
                  placeholder="time placeholder"
                  name="taskTime"
                  value={currentTask && currentTask.taskTime}
                  onChange={handleChange}
                  invalid={errors.taskTime ? true : false}
                />
                <FormFeedback>{errors.taskTime}</FormFeedback>
              </FormGroup>
            </div>
            <FormGroup>
              <Label>Assigned To</Label>
              <Select
                options={employees}
                value={currentTask && currentTask.employeeId}
                onChange={handleSelectChange}
                className={errors.employeeId ? "invalid" : ""}
              />
              <Input
                type="hidden"
                disabled
                invalid={errors.employeeId ? true : false}
              />
              <FormFeedback>{errors.employeeId}</FormFeedback>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="danger">Edit</Button>
          <Button color="success">Complete</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default Task;
