import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleCurrentEmployees, toggleAllEmployees } from '../../actions/employeesToggle';
import { useLocation } from 'react-router-dom';
import { Button, ButtonGroup } from 'reactstrap';

import '../../styles/sidebar.scss';

const EmployeesToggle = () => {
    const [hidden, setHidden] = useState(false);
    const all = useSelector(state => state.employeesToggleReducer.all);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/employees' || location.pathname === '/e_dashboard/employees/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className="task-btns">
            {hidden ? <ButtonGroup className="button-group">
                <Button onClick={() => dispatch(toggleCurrentEmployees())} active={all === false}>Current</Button>
                <Button onClick={() => dispatch(toggleAllEmployees())} active={all === true}>All</Button>
            </ButtonGroup> : null}
        </div>
    );
};

export default EmployeesToggle;
