import React, { useState } from "react";
import { Button } from "reactstrap";

import "../../styles/sidebar.scss";

const AddEventBtn = () => {
  const [event, setEvent] = useState({
    title: "",
    startDate: "",
    endDate: "",
    startTime: "",
    endTime: "",
    allDay: false,
    recurring: false,
    daysOfWeek: [],
    extendedProps: {
      location: "",
      inviteAll: false,
      invitees: [],
    },
  });
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    if (isOpen) {
      setEvent({
        title: "",
        startDate: "",
        endDate: "",
        startTime: "",
        endTime: "",
        allDay: false,
        recurring: false,
        daysOfWeek: [],
        extendedProps: {
          location: "",
          inviteAll: false,
          invitees: [],
        },
      });
    }

    setIsOpen(!isOpen);
  };

  return (
    <div className="add-event-div">
      <Button color="danger" onClick={toggle}>
        Add Event
      </Button>
    </div>
  );
};

export default AddEventBtn;
