import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormGroup,
    FormFeedback,
    Label,
    Input,
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddCustomerBtn = () => {
    const [customer, setCustomer] = useState({
        businessName: '',
        industry: '',
        primaryContact: {
            firstName: '',
            lastName: ''
        },
        billingContact: {
            firstName: '',
            lastName: ''
        },
        primaryPhoneNumber: '',
        primaryEmail: '',
        billingPhoneNumber: '',
        billingEmail: '',
        shopAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        billingAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
    });
    const [errors, setErrors] = useState({
        businessName: '',
        industry: '',
        primaryContact: {
            firstName: '',
            lastName: ''
        },
        billingContact: {
            firstName: '',
            lastName: ''
        },
        primaryPhoneNumber: '',
        primaryEmail: '',
        billingPhoneNumber: '',
        billingEmail: '',
        shopAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
        billingAddress: {
            address: '',
            city: '',
            state: '',
            zipcode: ''
        },
    });
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state);
    const getSuccess = useSelector(state => state);
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const isFirstRun = useRef(true);
    useEffect(() => {
        if(isFirstRun.current) {
            isFirstRun.current = false;
            return;
        }
        setErrors(getErrors);
    }, [getErrors]);

    useEffect(() => {
        if(getSuccess === true) {
            setSuccess(true);
        };
    }, [getSuccess]);

    useEffect(() => {
        if(success === true) {
            setIsOpen(false);
            setErrors({
                businessName: '',
                industry: '',
                primaryContact: {
                    firstName: '',
                    lastName: ''
                },
                billingContact: {
                    firstName: '',
                    lastName: ''
                },
                primaryPhoneNumber: '',
                primaryEmail: '',
                billingPhoneNumber: '',
                billingEmail: '',
                shopAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                billingAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
            })
            setSuccess(false);
            setCustomer({
                businessName: '',
                industry: '',
                primaryContact: {
                    firstName: '',
                    lastName: ''
                },
                billingContact: {
                    firstName: '',
                    lastName: ''
                },
                primaryPhoneNumber: '',
                primaryEmail: '',
                billingPhoneNumber: '',
                billingEmail: '',
                shopAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
                billingAddress: {
                    address: '',
                    city: '',
                    state: '',
                    zipcode: ''
                },
            });
        };
    }, [success]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/customers' || location.pathname === '/e_dashboard/customer/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        setIsOpen(!isOpen);
        setErrors({})
    };

    const handleChange = e => {
        setCustomer({
            ...customer,
            [e.target.name]: e.target.value
        });
    };

    const handlePrimaryContactChange = e => {
        setCustomer({
            ...customer,
            primaryContact: {
                ...customer.primaryContact,
                [e.target.name]: e.target.value
            }
        });
    };

    const handleBillingContactChange = e => {
        setCustomer({
            ...customer,
            billingContact: {
                ...customer.billingContact,
                [e.target.name]: e.target.value
            }
        });
    };

    const handleShopAddressChange = e => {
        setCustomer({
            ...customer,
            shopAddress: {
                ...customer.shopAddress,
                [e.target.name]: e.target.value
            }
        });
    };

    const handleBillingAddressChange = e => {
        setCustomer({
            ...customer,
            billingAddress: {
                ...customer.billingAddress,
                [e.target.name]: e.target.value
            }
        });
    };

    const handleSubmit = e => {
        e.preventDefault();
        // dispatch(registerCustomer(customer));
    };

    return (
        <div className="add-customer-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Customer</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Add Customer</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit} className="add-customer-form">
                        <FormGroup>
                            <Label for="businessName">Business Name</Label>
                            <Input type="text" name="businessName" onChange={handleChange} invalid={errors.businessName ? true : false} />
                            <FormFeedback>{errors.businessName}</FormFeedback>
                        </FormGroup>
                        <FormGroup>
                            <Label for="industry">Industry</Label>
                            <Input type="text" name="industry" onChange={handleChange} invalid={errors.industry ? true : false} />
                            <FormFeedback>{errors.industry}</FormFeedback>
                        </FormGroup>
                        <Label for="primary-contact" className="form-label">Primary Contact</Label>
                        <div className="primary-contact-name">
                            <FormGroup>
                                <Label for="firstName">First Name</Label>
                                <Input type="text" name="firstName" onChange={handlePrimaryContactChange} invalid={errors.primaryContact ? errors.primaryContact.firstName ? true : false : false}  />
                                <FormFeedback>{errors.primaryContact ? errors.primaryContact.firstName ? errors.primaryContact.firstName : null : null}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="lastName">Last Name</Label>
                                <Input type="text" name="lastName" onChange={handlePrimaryContactChange} invalid={errors.primaryContact ? errors.primaryContact.lastName ? true : false : false} />
                                <FormFeedback>{errors.primaryContact ? errors.primaryContact.lastName ? errors.primaryContact.lastName : null : null}</FormFeedback>
                            </FormGroup>
                        </div>
                        <div className="primary-phone-email-div">
                            <FormGroup>
                                <Label for="primaryPhoneNumber">Phone Number</Label>
                                <Input type="text" name="primaryPhoneNumber" onChange={handleChange} invalid={errors.primaryPhoneNumber ? true : false} />
                                <FormFeedback>{errors.primaryPhoneNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="primaryEmail">Email</Label>
                                <Input type="text" name="primaryEmail" onChange={handleChange} invalid={errors.primaryEmail ? true : false} />
                                <FormFeedback>{errors.primaryEmail}</FormFeedback>
                            </FormGroup>
                        </div>
                        <Label for="shop-address" className="form-label">Shop Address</Label>
                        <div className="shop-address">
                            <div className="shop-address-city-div">
                                <FormGroup>
                                    <Label for="address">Address</Label>
                                    <Input type="text" name="address" onChange={handleShopAddressChange} invalid={errors.shopAddress ? errors.shopAddress.address ? true : false : false} />
                                    <FormFeedback>{errors.shopAddress ? errors.shopAddress.address ? errors.shopAddress.address : null : null}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="city">City</Label>
                                    <Input type="text" name="city" onChange={handleShopAddressChange} invalid={errors.shopAddress ? errors.shopAddress.city ? true : false : false} />
                                    <FormFeedback>{errors.shopAddress ? errors.shopAddress.city ? errors.shopAddress.city : null : null}</FormFeedback>
                                </FormGroup>
                            </div>
                            <div className="shop-state-zipcode-div">
                                <FormGroup>
                                    <Label for="state">State</Label>
                                    <Input type="text" name="state" onChange={handleShopAddressChange} invalid={errors.shopAddress ? errors.shopAddress.state ? true : false : false} />
                                    <FormFeedback>{errors.shopAddress ? errors.shopAddress.state ? errors.shopAddress.state : null : null}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="zipcode">Zipcode</Label>
                                    <Input type="text" name="zipcode" onChange={handleShopAddressChange} invalid={errors.shopAddress ? errors.shopAddress.zipcode ? true : false : false} />
                                    <FormFeedback>{errors.shopAddress ? errors.shopAddress.zipcode ? errors.shopAddress.zipcode : null : null}</FormFeedback>
                                </FormGroup>
                            </div>
                        </div>
                        <Label for="billing-contact" className="form-label">Billing Contact</Label>
                        <div className="billing-contact-name">
                            <FormGroup>
                                <Label for="firstName">First Name</Label>
                                <Input type="text" name="firstName" onChange={handleBillingContactChange} invalid={errors.billingContact ? errors.billingContact.firstName ? true : false : false} />
                                <FormFeedback>{errors.billingContact ? errors.billingContact.firstName ? errors.billingContact.firstName : null : null}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="lastName">Last Name</Label>
                                <Input type="text" name="lastName" onChange={handleBillingContactChange} invalid={errors.billingContact ? errors.billingContact.lastName ? true : false : false} />
                                <FormFeedback>{errors.billingContact ? errors.billingContact.lastName ? errors.billingContact.lastName : null : null}</FormFeedback>
                            </FormGroup>
                        </div>
                        <div className="billing-phone-email-div">
                            <FormGroup>
                                <Label for="billingPhoneNumber">Phone Number</Label>
                                <Input type="text" name="billingPhoneNumber" onChange={handleChange} invalid={errors.billingPhoneNumber} />
                                <FormFeedback>{errors.billingPhoneNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="billingEmail">Email</Label>
                                <Input type="text" name="billingEmail" onChange={handleChange} invalid={errors.billingEmail ? true : false} />
                                <FormFeedback>{errors.billingEmail}</FormFeedback>
                            </FormGroup>
                        </div>
                        <Label for="billing-address" className="form-label">Billing Address</Label>
                        <div className="billing-address">
                            <div className="billing-address-city-div">
                                <FormGroup>
                                    <Label for="address">Address</Label>
                                    <Input type="text" name="address" onChange={handleBillingAddressChange} invalid={errors.billingAddress ? errors.billingAddress.address ? true : false : false} />
                                    <FormFeedback>{errors.billingAddress ? errors.billingAddress.address ? errors.billingAddress.address : null : null}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="city">City</Label>
                                    <Input type="text" name="city" onChange={handleBillingAddressChange} invalid={errors.billingAddress ? errors.billingAddress.city ? true : false : false} />
                                    <FormFeedback>{errors.billingAddress ? errors.billingAddress.city ? errors.billingAddress.city : null : null}</FormFeedback>
                                </FormGroup>
                            </div>
                            <div className="billing-state-zipcode-div">
                                <FormGroup>
                                    <Label for="state">State</Label>
                                    <Input type="text" name="state" onChange={handleBillingAddressChange} invalid={errors.billingAddress ? errors.billingAddress.state ? true : false : false} />
                                    <FormFeedback>{errors.billingAddress ? errors.billingAddress.state ? errors.billingAddress.state : null : null}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="zipcode">Zipcode</Label>
                                    <Input type="text" name="zipcode" onChange={handleBillingAddressChange} invalid={errors.billingAddress ? errors.billingAddress.zipcode ? true : false : false} />
                                    <FormFeedback>{errors.billingAddress ? errors.billingAddress.zipcode ? errors.billingAddress.zipcode : null : null}</FormFeedback>
                                </FormGroup>
                            </div>
                        </div>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" onClick={handleSubmit}>Submit</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default AddCustomerBtn;
