export {
    LOGIN_START,
    LOGIN_SUCCESS,
    LOGIN_FAILED,
    login
} from './login';

export {
    CALENDAR_VIEW,
    LIST_VIEW,
    toggleCalendarView,
    toggleListView
} from './taskToggle';

export {
    OPEN_WORK_ORDERS,
    CLOSE_WORK_ORDERS,
    openWorkOrders,
    closeWorkOrders
} from './workOrderToggle';

export {
    CURRENT_EMPLOYEES,
    ALL_EMPLOYEES,
    toggleCurrentEmployees,
    toggleAllEmployees
} from './employeesToggle';

export {
    GET_TASKS_START,
    GET_TASKS_SUCCESS,
    GET_TASKS_FAILURE,
    getTasks
} from './getTasks';

export {
    GET_EMPLOYEES_START,
    GET_EMPLOYEES_SUCCESS,
    GET_EMPLOYEES_FAILURE,
    getEmployees
} from './getEmployees';

export {
    GET_CUSTOMERS_START,
    GET_CUSTOMERS_SUCCESS,
    GET_CUSTOMERS_FAILURE,
    getCustomers
} from './getCustomers';

export {
    GET_USERS_START,
    GET_USERS_SUCCESS,
    GET_USERS_FAILURE,
    getUsers
} from './getUsers';

export {
    REGISTER_EMPLOYEE_START,
    REGISTER_EMPLOYEE_SUCCESS,
    REGISTER_EMPLOYEE_FAILURE,
    registerEmployee
} from './registerEmployee';

export {
    REGISTER_CUSTOMER_START,
    REGISTER_CUSTOMER_SUCCESS,
    REGISTER_CUSTOMER_FAILURE,
    registerCustomer
} from './registerCustomer';

export {
    EDIT_EMPLOYEE_START,
    EDIT_EMPLOYEE_SUCCESS,
    EDIT_EMPLOYEE_FAILURE,
    editEmployee
} from './editEmployee';

export {
    EDIT_CUSTOMER_START,
    EDIT_CUSTOMER_SUCCESS,
    EDIT_CUSTOMER_FAILURE,
    editCustomer
} from './editCustomer';

export {
    CHANGE_EMPLOYEE_STATUS_START,
    CHANGE_EMPLOYEE_STATUS_SUCCESS,
    CHANGE_EMPLOYEE_STATUS_FAILURE,
    changeEmployeeStatus
} from './changeEmployeeStatus';

export {
    GET_WORK_ORDERS_START,
    GET_WORK_ORDERS_SUCCESS,
    GET_WORK_ORDERS_FAILURE,
    getWorkOrders
} from './getWorkOrders';

export {
    ADD_WORK_ORDER_START,
    ADD_WORK_ORDER_SUCCESS,
    ADD_WORK_ORDER_FAILURE,
    addWorkOrder
} from './addWorkOrder';

export {
    GET_WORK_ORDER_START,
    GET_WORK_ORDER_SUCCESS,
    GET_WORK_ORDER_FAILURE,
    getWorkOrder
} from './getWorkOrder';

export {
    GET_CUSTOMER_START,
    GET_CUSTOMER_SUCCESS,
    GET_CUSTOMER_FAILURE,
    getCustomer
} from './getCustomer';

export {
    ADD_EMPLOYEE_START,
    ADD_EMPLOYEE_SUCCESS,
    ADD_EMPLOYEE_FAILURE,
    addEmployeeToCustomer
} from './addEmployeeToCustomer';

export {
    GET_CUSTOMER_TASKS_START,
    GET_CUSTOMER_TASKS_SUCCESS,
    GET_CUSTOMER_TASKS_FAILURE,
    getCustomerTasks
} from './getCustomerTasks';

export {
    ADD_TASK_START,
    ADD_TASK_SUCCESS,
    ADD_TASK_FAILURE,
    addTask
} from './addTask';

export {
    GET_CUSTOMER_NOTES_START,
    GET_CUSTOMER_NOTES_SUCCESS,
    GET_CUSTOMER_NOTES_FAILURE,
    getCustomerNotes
} from './getCustomerNotes';

// const dbUrl = 'http://localhost:5000/rocky-mountain-lifts-crm-db/us-central1/api'; // Development URL
const dbUrl = 'https://us-central1-rocky-mountain-lifts-crm-db.cloudfunctions.net/api'; // Production URL

export { dbUrl };
