import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    CardText,
    Table
} from 'reactstrap';

import '../../styles/employee-dashboard.scss';

const OpenWorkOrders = () => {
    const [workOrders, setWorkOrders] = useState([]);
    const getWorkOrders = useSelector(state => state.getWorkOrdersReducer.workOrders);

    useEffect(() => {
        setWorkOrders(getWorkOrders.workOrders);
    }, [getWorkOrders]);

    console.log(workOrders)

    return (
        <div>
            <h1>Open Work Orders</h1>
            {workOrders ? 
                workOrders.map(workOrder => (
                    <Card>
                        <CardBody>
                        <Table>
                            <thead>
                                <tr>
                                    <th>Job #</th>
                                    <th>Customer Name</th>
                                    <th>Service Type</th>
                                    <th>Service Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <th>{workOrder.jobNumber}</th>
                                    <td>{workOrder.customerName}</td>
                                    <td>{workOrder.serviceType}</td>
                                    <td>{workOrder.serviceDate}</td>
                                </tr>
                            </tbody>
                        </Table>
                        </CardBody>
                    </Card>
                ))
            : null}
        </div>
    );
};

export default OpenWorkOrders;
