import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import {
    TabContent,
    TabPane,
    Nav,
    NavItem,
    NavLink,
    Form,
    FormGroup,
    Label,
    Input,
    FormFeedback,
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Table,
    Card
} from 'reactstrap';
import Select from 'react-select';
import EmployeeNavbar from './EmployeeNavbar';
import EmployeeSidebar from './EmployeeSidebar';
import Footer from './Footer';
import { getCustomer } from '../../actions/getCustomer';
import { addEmployeeToCustomer } from '../../actions/addEmployeeToCustomer';
import { getCustomerTasks } from '../../actions/getCustomerTasks';
import { getUsers } from '../../actions/getUsers';
import { addTask } from '../../actions/addTask';
import { getCustomerNotes } from '../../actions/getCustomerNotes';

import '../../styles/employee-dashboard.scss'
import classnames from 'classnames';

const CustomerPage = () => {
    const [customer, setCustomer] = useState({});
    const getCustomerFromState = useSelector(state => state.getCustomerReducer.customer);
    const [activeTab, setActiveTab] = useState('1');
    const [addEmployeeModalOpen, setAddEmployeeModalOpen] = useState(false);
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        jobTitle: '',
        officeNumber: '',
        cellNumber: '',
        email: ''
    });
    const [addEmployeeErrors, setAddEmployeeErrors] = useState({});
    const [flag, setFlag] = useState(false);
    const [tasks, setTasks] = useState([]);
    const getTasksFromState = useSelector(state => state.getCustomerTasksReducer.tasks);
    const [addTaskModalOpen, setAddTaskModalOpen] = useState(false);
    const [task, setTask] = useState({
        taskDate: '',
        taskTime: '',
        taskDescription: '',
        customerId: '',
        employeeId: ''
    });
    const [employees, setEmployees] = useState([]);
    const employeeArray = [];
    const getUsersFromState = useSelector(state => state.getUsersReducer.users);
    const getAddTaskSuccess = useSelector(state => state.addTaskReducer.success);
    const [addTaskErrors, setAddTaskErrors] = useState({});
    const getAddTaskErrors = useSelector(state => state.addTaskReducer.errors);
    const [notes, setNotes] = useState([]);
    const getNotesFromState = useSelector(state => state.getCustomerNotesReducer.notes);
    const [addNoteModalOpen, setAddNoteModalOpen] = useState(false);
    const [note, setNote] = useState({
        note: '',
        createdAt: '',
        createdBy: ''
    });
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const customerId = pathArray[pathArray.length - 1];
        dispatch(getCustomer(customerId));
        dispatch(getCustomerTasks(customerId));
        dispatch(getUsers());
        dispatch(getCustomerNotes(customerId));
    }, []);

    useEffect(() => {
        if(getCustomerFromState) {
            setCustomer(getCustomerFromState);
            setTask({
                ...task,
                customerId: getCustomerFromState.userId
            })
        };
    }, [getCustomerFromState]);

    useEffect(() => {
        if(getTasksFromState) {
            setTasks(getTasksFromState);
        };
    }, [getTasksFromState]);

    useEffect(() => {
        if(getNotesFromState) {
            console.log(getNotesFromState)
            setNotes(getNotesFromState);
        };
    }, [getNotesFromState]);

    useEffect(() => {
        if(flag) {
            setFlag(false);
            dispatch(addEmployeeToCustomer(customer));
            toggleAddEmployeeModal();
        };
    }, [flag]);

    useEffect(() => {
        if(getUsersFromState.users) {
            getUsersFromState.users.map(user => {
                if(user.userType === 'employee' && user.active === true) {
                    employeeArray.push({
                        label: `${user.firstName} ${user.lastName}`,
                        value: user.userId,
                        name: 'employeeId',
                        jobTitle: user.jobTitle
                    });
                };
            });

            setEmployees(employeeArray);
        };
    }, [getUsersFromState]);

    useEffect(() => {
        if(getAddTaskSuccess) {
            console.log(getAddTaskSuccess)
            toggleAddTaskModal();
        };
    }, [getAddTaskSuccess]);

    useEffect(() => {
        if(getAddTaskErrors) {
            setAddTaskErrors(getAddTaskErrors);
        };
    }, [getAddTaskErrors]);

    const dateConverter = date => {
        const dateArray = date.split('-');
        return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
    };

    const timeConverter = time => {
        const timeArray = time.split(':');
        const hour = parseInt(timeArray[0]);
        let m;

        if(hour <= 11) {
            m = 'AM';
        } else {
            m = 'PM';
        };

        if(hour === 0) {
            const newHour = '12';
            timeArray[0] = newHour;
        } else if(hour <= 12) {
            const newHour = hour.toString();
            timeArray[0] = newHour;
        } else {
            const newHour = (hour - 12).toString();
            timeArray[0] = newHour;
        };

        return `${timeArray[0]}:${timeArray[1]} ${m}`;
    };

    const employeeFinder = employeeId => {
        employees.forEach(employee => {
            if(employee.value === employeeId) {
                return employee.label;
            };
        });
    };

    const toggleTab = tab => {
        if(activeTab !== tab) {
            setActiveTab(tab);
        };
    };

    const toggleAddEmployeeModal = () => {
        if(addEmployeeModalOpen) {
            setEmployee({
                firstName: '',
                lastName: '',
                jobTitle: '',
                officeNumber: '',
                cellNumber: '',
                email: ''
            });
            setAddEmployeeErrors({});
        };
        setAddEmployeeModalOpen(!addEmployeeModalOpen);
    };

    const toggleAddTaskModal = () => {
        if(addTaskModalOpen) {
            setTask({
                ...task,
                taskDate: '',
                taskTime: '',
                taskDescription: '',
                employeeId: ''
            });
            setAddTaskErrors({});
        };
        setAddTaskModalOpen(!addTaskModalOpen);
    };

    const toggleAddNoteModal = () => {
        if(addNoteModalOpen) {
            setNote({
                note: '',
                createdAt: '',
                createdBy: ''
            });
        };
        setAddNoteModalOpen(!addNoteModalOpen);
    };

    const handleAddEmployeeChange = e => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    };

    const handleAddTaskChange = e => {
        setTask({
            ...task,
            [e.target.name]: e.target.value
        });
    };

    const handleAddTaskSelectChange = e => {
        setTask({
            ...task,
            [e.name]: e.value
        });
    };

    const handleAddNoteChange = e => {
        setNote({
            ...note,
            [e.target.name]: e.target.value
        });
    };

    const handleAddEmployeeSubmit = e => {
        e.preventDefault();

        let errors = {};

        if(employee.firstName === '') {
            errors.firstName = 'Must not be empty';
        };
        if(employee.lastName === '') {
            errors.lastName = 'Must not be empty';
        };
        if(employee.jobTitle === '') {
            errors.jobTitle = 'Must not be empty';
        };
        if(employee.officeNumber === '' && employee.cellNumber === '') {
            errors.officeNumber = 'Must not be empty';
            errors.cellNumber = 'Must not be empty'
        } else if(employee.officeNumber !== '' && !employee.officeNumber.match(/^[0-9]{3}[-]{1}[0-9]{3}[-]{1}[0-9]{4}$/)) {
            errors.officeNumber = 'Must be a valid phone number';
        } else if(employee.cellNumber !== '' && !employee.cellNumber.match(/^[0-9]{3}[-]{1}[0-9]{3}[-]{1}[0-9]{4}$/)) {
            errors.cellNumber = 'Must be a valid phone number';
        };
        if(employee.email === '') {
            errors.email = 'Must not be empty';
        } else if(!employee.email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
            errors.email = 'Must be a valid email address';
        };

        setAddEmployeeErrors(errors);

        if(Object.keys(errors).length === 0) {
            if(customer.employees.length === 0) {
                setFlag(true);
                setCustomer({
                    ...customer,
                    employees: [employee]
                });
            } else {
                setFlag(true);
                setCustomer({
                    ...customer,
                    employees: [...customer.employees, employee]
                });
            };
        };
    };

    const handleAddTaskSubmit = e => {
        e.preventDefault();
        dispatch(addTask(task));
    };

    const handleAddNoteSubmit = e => {
        e.preventDefault();
        console.log("Note Submitted");
    };

    return (
        <div>
            <EmployeeNavbar />
            <div className="customer-page">
                <EmployeeSidebar />
                <div className="customer-page-content">
                    <h1>{customer.businessName}</h1>
                    <Nav tabs>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '1' })} onClick={() => { toggleTab('1'); }}>
                                Info
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '2' })} onClick={() => { toggleTab('2') }}>
                                Tasks
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '3' })} onClick={() => { toggleTab('3') }}>
                                Appointments
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '4' })} onClick={() => { toggleTab('4') }}>
                                Notes
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '5' })} onClick={() => { toggleTab('5') }}>
                                Employees
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '6' })} onClick={() => { toggleTab('6') }}>
                                Lifts
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink className={classnames({ active: activeTab === '7' })} onClick={() => { toggleTab('7') }}>
                                Documents
                            </NavLink>
                        </NavItem>
                    </Nav>
                    <TabContent activeTab={activeTab}>
                        <TabPane tabId="1">
                            <div>
                                <Form className="customer-page-info-tab">
                                    <div className="customer-page-contacts">
                                        <FormGroup>
                                            <h3>Primary Contact</h3>
                                            <div>
                                                <p>Name: <span>{customer.primaryContact ? customer.primaryContact.firstName : null} {customer.primaryContact ? customer.primaryContact.lastName : null}</span></p>
                                                <p>Job Title: <span>{customer.primaryJobTitle}</span></p>
                                                <p>Office: <a href={`tel:+1-${customer.primaryOfficeNumber}`}>{customer.primaryOfficeNumber}</a></p>
                                                <p>Cell: <a href={`tel:+1-${customer.primaryCellNumber}`}>{customer.primaryCellNumber}</a></p>
                                                <p>Email: <a href={`mailto:${customer.primaryEmail}`}>{customer.primaryEmail}</a></p>
                                            </div>
                                        </FormGroup>
                                        <FormGroup>
                                            <h3>Billing Contact</h3>
                                            <div>
                                                <p>Name: <span>{customer.billingContact ? customer.billingContact.firstName : null} {customer.billingContact ? customer.billingContact.lastName : null}</span></p>
                                                <p>Job Title: <span>{customer.billingJobTitle}</span></p>
                                                <p>Office: <a href={`tel:+1-${customer.billingOfficeNumber}`}>{customer.billingOfficeNumber}</a></p>
                                                <p>Cell: <a href={`tel:+1-${customer.billingCellNumber}`}>{customer.billingCellNumber}</a></p>
                                                <p>Email: <a href={`mailto:${customer.billingEmail}`}>{customer.billingEmail}</a></p>
                                            </div>
                                        </FormGroup>
                                    </div>
                                    <div className="customer-page-addresses">
                                        <FormGroup>
                                            <h3>Shop Address</h3>
                                            <a href={`http://maps.google.com/?q=${customer.shopAddress ? customer.shopAddress.address : null}, ${customer.shopAddress ? customer.shopAddress.city : null}, ${customer.shopAddress ? customer.shopAddress.state : null} ${customer.shopAddress ? customer.shopAddress.zipcode : null}`} target="_blank" rel="noreferrer" className="address-link">
                                                <p>{customer.shopAddress ? customer.shopAddress.address : null},</p>
                                                <p>{customer.shopAddress ? customer.shopAddress.city : null}, {customer.shopAddress ? customer.shopAddress.state : null}</p>
                                                <p>{customer.shopAddress ? customer.shopAddress.zipcode : null}</p>
                                            </a>
                                        </FormGroup>
                                        <FormGroup>
                                            <h3>Billing Address</h3>
                                            <a href={`http://maps.google.com/?q=${customer.billingAddress ? customer.billingAddress.address : null}, ${customer.billingAddress ? customer.billingAddress.city : null}, ${customer.billingAddress ? customer.billingAddress.state : null} ${customer.billingAddress ? customer.billingAddress.zipcode : null}`} target="_blank" rel="noreferrer" className="address-link">
                                                <p>{customer.billingAddress ? customer.billingAddress.address : null},</p>
                                                <p>{customer.billingAddress ? customer.billingAddress.city : null}, {customer.billingAddress ? customer.billingAddress.state : null}</p>
                                                <p>{customer.billingAddress ? customer.billingAddress.zipcode : null}</p>
                                            </a>
                                        </FormGroup>
                                    </div>
                                </Form>
                            </div>
                        </TabPane>
                        <TabPane tabId="2">
                            <div className="customer-page-task-tab">
                                <Table borderless className="task-table-header">
                                    <thead>
                                        <tr className="table-headers">
                                            <th>Task</th>
                                            <th>Date</th>
                                            <th>Time</th>
                                        </tr>
                                    </thead>
                                </Table>
                                    {tasks ? tasks.tasks ? tasks.tasks.map(task => (
                                        <Card className="task-card">
                                            <Table borderless className="task-card-table">
                                            <tbody>
                                                <tr className="table-data">
                                                    <td>{task.taskDescription}</td>
                                                    <td>{dateConverter(task.taskDate)}</td>
                                                    <td>{timeConverter(task.taskTime)}</td>
                                                </tr>
                                            </tbody>
                                            </Table>
                                        </Card>
                                    )) : null : null}
                                <Button outline color="success" onClick={toggleAddTaskModal}>Add Task</Button>
                            </div>
                        </TabPane>
                        <TabPane tabId="3">
                            <span>Appointments</span>
                        </TabPane>
                        <TabPane tabId="4">
                            <div className="customer-page-note-tab">
                                {notes ? notes.notes ? notes.notes.map(note => (
                                    <Card className="note-card">
                                        <p>{note.note}</p>
                                        <div>
                                            <p>Created By: <span>{(note) => {
                                                const name = employeeFinder(note.createdBy);
                                                console.log(name)
                                            }}</span></p>
                                            <p>Created At: {note.createdAt}</p>
                                        </div>
                                    </Card>
                                )) : null : null}
                                <Button outline color="success" onClick={toggleAddNoteModal}>Add Note</Button> 
                            </div>
                        </TabPane>
                        <TabPane tabId="5">
                            <Table size="sm" hover className="customer-page-employees-table">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Job Title</th>
                                        <th>Office Number</th>
                                        <th>Cell Number</th>
                                        <th>Email</th> 
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{customer.primaryContact ? customer.primaryContact.firstName : null} {customer.primaryContact ? customer.primaryContact.lastName : null}</td>
                                        <td>{customer.primaryJobTitle}</td>
                                        <td><a href={`tel:+1-${customer.primaryOfficeNumber}`}>{customer.primaryOfficeNumber}</a></td>
                                        <td><a href={`tel:+1-${customer.primaryCellNumber}`}>{customer.primaryCellNumber}</a></td>
                                        <td><a href={`mailto:${customer.primaryEmail}`}>{customer.primaryEmail}</a></td>
                                    </tr>
                                    {customer.billingJobTitle !== '' ?
                                        <tr>
                                            <td>{customer.billingContact ? customer.billingContact.firstName : null} {customer.billingContact ? customer.billingContact.lastName : null}</td>
                                            <td>{customer.billingJobTitle}</td>
                                            <td><a href={`tel:+1-${customer.billingOfficeNumber}`}>{customer.billingOfficeNumber}</a></td>
                                            <td><a href={`tel:+1-${customer.billingCellNumber}`}>{customer.billingCellNumber}</a></td>
                                            <td><a href={`mailto:${customer.billingEmail}`}>{customer.billingEmail}</a></td>
                                        </tr>
                                    : null }
                                    {customer.employees ? customer.employees.map(employee => (
                                        <tr>
                                            <td>{employee.firstName} {employee.lastName}</td>
                                            <td>{employee.jobTitle}</td>
                                            <td><a href={`tel:+1-${employee.officeNumber}`}>{employee.officeNumber}</a></td>
                                            <td><a href={`tel:+1-${employee.cellNumber}`}>{employee.cellNumber}</a></td>
                                            <td><a href={`mailto:${employee.email}`}>{employee.email}</a></td>
                                        </tr>
                                    )) : null}
                                </tbody>
                            </Table>
                            <div className="add-employee-div">
                                <Button outline color="success" onClick={toggleAddEmployeeModal}>Add Employee</Button>
                            </div>
                        </TabPane>
                        <TabPane tabId="6">
                            <span>Lifts</span>
                        </TabPane>
                        <TabPane tabId="7">
                            <span>Coming Soon!!!</span>
                        </TabPane>
                    </TabContent>
                </div>
                <Modal isOpen={addEmployeeModalOpen} toggle={toggleAddEmployeeModal}>
                    <ModalHeader toggle={toggleAddEmployeeModal}>Add Employee</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleAddEmployeeSubmit}>
                            <div className="add-employee-name-div">
                                <FormGroup>
                                    <Label for="employeeFirstName">First Name</Label>
                                    <Input type="text" name="firstName" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.firstName ? true : false} />
                                    <FormFeedback>{addEmployeeErrors.firstName}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="employeeLastName">Last Name</Label>
                                    <Input type="text" name="lastName" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.lastName ? true : false} />
                                    <FormFeedback>{addEmployeeErrors.lastName}</FormFeedback>
                                </FormGroup>
                            </div>
                            <FormGroup>
                                <Label for="employeeJobTitle">Job Title</Label>
                                <Input type="text" name="jobTitle" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.jobTitle ? true : false} />
                                <FormFeedback>{addEmployeeErrors.jobTitle}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="employeeOfficeNumber">Office Phone Number</Label>
                                <Input type="text" name="officeNumber" placeholder="XXX-XXX-XXXX" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.officeNumber ? true : false} />
                                <FormFeedback>{addEmployeeErrors.officeNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="employeeCellNumber">Cell Phone Number</Label>
                                <Input type="text" name="cellNumber" placeholder="XXX-XXX-XXXX" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.cellNumber ? true : false} />
                                <FormFeedback>{addEmployeeErrors.cellNumber}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="employeeEmail">Email</Label>
                                <Input type="text" name="email" placeholder="example@example.com" onChange={handleAddEmployeeChange} invalid={addEmployeeErrors.email ? true : false} />
                                <FormFeedback>{addEmployeeErrors.email}</FormFeedback>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleAddEmployeeSubmit}>Submit</Button>
                    </ModalFooter>
                </Modal>
                <Modal isOpen={addTaskModalOpen} toggle={toggleAddTaskModal}>
                    <ModalHeader toggle={toggleAddTaskModal}>Add Task</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleAddTaskSubmit}>
                            <div className="add-task-date-div">
                                <FormGroup>
                                    <Label for="taskDate">Date</Label>
                                    <Input type="date" name="taskDate" placeholder="date placeholder" onChange={handleAddTaskChange} invalid={addTaskErrors.taskDate ? true : false} />
                                    <FormFeedback>{addTaskErrors.taskDate}</FormFeedback>
                                </FormGroup>
                                <FormGroup>
                                    <Label for="taskTime">Time</Label>
                                    <Input type="time" name="taskTime" placeholder="time placeholder" onChange={handleAddTaskChange} invalid={addTaskErrors.taskTime ? true : false} />
                                    <FormFeedback>{addTaskErrors.taskTime}</FormFeedback>
                                </FormGroup>
                            </div>
                            <FormGroup>
                                <Label for="taskDescription">Task Description</Label>
                                <Input type="textarea" name="taskDescription" onChange={handleAddTaskChange} invalid={addTaskErrors.taskDescription ? true : false} />
                                <FormFeedback>{addTaskErrors.taskDescription}</FormFeedback>
                            </FormGroup>
                            <FormGroup>
                                <Label for="employeeId">Assigned To</Label>
                                <Select name="employeeId" options={employees} onChange={handleAddTaskSelectChange} className={addTaskErrors.employeeId ? 'invalid' : ''} />
                                <Input type="hidden" disabled invalid={addTaskErrors.employeeId ? true : false} />
                                <FormFeedback>{addTaskErrors.employeeId}</FormFeedback>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleAddTaskSubmit}>Submit</Button>
                    </ModalFooter>
                </Modal>
                <Modal isOpen={addNoteModalOpen} toggle={toggleAddNoteModal}>
                    <ModalHeader toggle={toggleAddNoteModal}>Add Note</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleAddNoteSubmit}>
                            <FormGroup>
                                <Label for="note">Note</Label>
                                <Input type="textarea" name="note" onChange={handleAddNoteChange} />
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleAddNoteSubmit}>Submit</Button>
                    </ModalFooter>
                </Modal>
            </div>
            <Footer />
        </div>
    );
};

export default CustomerPage;
