import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { openWorkOrders, closeWorkOrders } from "../../actions/workOrderToggle";
import { useLocation } from "react-router-dom";
import { Button, ButtonGroup } from "reactstrap";

import "../../styles/sidebar.scss";

const WorkOrderToggle = () => {
  const [hidden, setHidden] = useState(true);
  const open = useSelector((state) => state.workOrderToggleReducer.open);
  const dispatch = useDispatch();
  const location = useLocation();

  useEffect(() => {
    changeHidden(location);
  }, [location]);

  const changeHidden = (location) => {
    if (
      location.pathname === "/dashboard/workorders" ||
      location.pathname === "/dashboard/workorders/"
    ) {
      setHidden(false);
    } else {
      setHidden(true);
    }
  };

  return (
    <div className={hidden ? "" : "task-btns"}>
      {hidden ? null : (
        <ButtonGroup>
          <Button
            onClick={() => dispatch(openWorkOrders())}
            active={open === true}
          >
            Open
          </Button>
          <Button
            onClick={() => dispatch(closeWorkOrders())}
            active={open === false}
          >
            Closed
          </Button>
        </ButtonGroup>
      )}
    </div>
  );
};

export default WorkOrderToggle;
