import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { 
    Button, 
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormGroup,
    Label,
    Input, 
} from 'reactstrap';
import { useLocation } from 'react-router-dom';
import { registerEmployee } from '../../actions/registerEmployee';

import '../../styles/sidebar.scss';

const AddEmployeeBtn = () => {
    const [credentials, setCredentials] = useState({
        firstName: '',
        lastName: '',
        jobTitle: 'Service Technician',
        tierLevel: 1,
        phoneNumber: '',
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [errors, setErrors] = useState({});
    const getErrors = useSelector(state => state.registerEmployeeReducer.errors)
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const isFirstRun = useRef(true);
    useEffect(() => {
        if(isFirstRun.current) {
            isFirstRun.current = false;
            return;
        }
        setErrors(getErrors);
        console.log(errors)
    })

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/employees' || location.pathname === '/e_dashboard/employees/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        setIsOpen(!isOpen);
    };

    const handleChange = e => {
        console.log(e.target.value)
        setCredentials({
            ...credentials,
            [e.target.name]: e.target.value
        });
        console.log(credentials[e.target.name])
    };

    const handleSubmit = e => {
        e.preventDefault();
        dispatch(registerEmployee(credentials));
        let noErrors = window.setTimeout(checkForErrors(), 2000);
        if(noErrors) {
            toggle();
        };
    };

    const checkForErrors = () => {
        if(Object.keys(errors).length === 0) {
            return true;
        } else {
            return false;
        };
    };

    const closeBtn = <Button className="close" onClick={toggle}>&times;</Button>;

    return (
        <div className="add-employee-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Employee</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle} close={closeBtn}>Add Employee</ModalHeader>
                <ModalBody>
                   <Form onSubmit={handleSubmit}>
                       <div className="name-div">
                           <FormGroup>
                               <Label for="firstName">First Name</Label>
                               <Input type="text" name="firstName" id="firstName" onChange={handleChange} />
                           </FormGroup>
                           <FormGroup>
                               <Label for="lastName">Last Name</Label>
                               <Input type="text" name="lastName" id="lastName" onChange={handleChange} />
                           </FormGroup>
                       </div>
                       <div className="job-info-div">
                           <FormGroup className="job-title-form-group">
                               <Label for="jobTitle">Job Title</Label>
                               <Input type="select" name="jobTitle" id="jobTitle" onChange={handleChange}>
                                   <option>Service Technician</option>
                                   <option>Outside Sales</option>
                                   <option>Service Manager</option>
                                   <option>Dispatch</option>
                                   <option>Office Manager</option>
                                   <option>Owner</option>
                               </Input>
                           </FormGroup>
                           <FormGroup className="tier-level-form-group">
                               <Label for="tierLevel">Tier Level</Label>
                               <Input type="select" name="tierLevel" id="tierLevel" onChange={handleChange}>
                                   <option>1</option>
                                   <option>2</option>
                                   <option>3</option>
                               </Input>
                           </FormGroup>
                       </div>
                       <FormGroup>
                           <Label for="phoneNumber">Phone Number</Label>
                           <Input type="text" name="phoneNumber" id="phoneNumber" placeholder="XXX-XXX-XXXX" onChange={handleChange} />
                       </FormGroup>
                       <FormGroup>
                           <Label for="email">Email</Label>
                           <Input type="email" name="email" id="email" placeholder="example@example.com" onChange={handleChange} />
                       </FormGroup>
                       <div className="password-div">
                           <FormGroup>
                               <Label for="password">Password</Label>
                               <Input type="password" name="password" id="password" onChange={handleChange} />
                           </FormGroup>
                           <FormGroup>
                               <Label for="confirmPassword">Confirm Password</Label>
                               <Input type="password" name="confirmPassword" id="confirmPassword" onChange={handleChange} />
                           </FormGroup>
                       </div>
                   </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" type="submit" onClick={handleSubmit}>Submit</Button>
                </ModalFooter>
            </Modal> 
        </div>
    );
};

export default AddEmployeeBtn;
