import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Button, Card, CardHeader, CardBody, Modal, ModalHeader, ModalBody, ModalFooter, Table } from "reactstrap";
import { getTasks } from "../../actions/getTasks";
import { getWorkOrders } from "../../actions/getWorkOrders";

import "../../styles/task.scss";

const Task = () => {
  const [tasks, setTasks] = useState([]);
  const fetchTasks = useSelector((state) => state.getTasksReducer.tasks);
  const [workOrders, setWorkOrders] = useState([]);
  const fetchWorkOrders = useSelector(
    (state) => state.getWorkOrdersReducer.workOrders
  );
  const [isOpen, setIsOpen] = useState(false);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getTasks())
    dispatch(getWorkOrders());
  }, []);

  useEffect(() => {
    setTasks(fetchTasks.tasks);
  }, [fetchTasks]);

  useEffect(() => {
    setWorkOrders(fetchWorkOrders.workOrders);
  }, [fetchWorkOrders]);

  const dateConverter = (date) => {
    const dateArray = date.split("-");
    return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
  };

  const timeConverter = (time) => {
    const timeArray = time.split(":");
    const hour = parseInt(timeArray[0]);
    let m;

    if (hour <= 11) {
      m = "AM";
    } else {
      m = "PM";
    }

    if (hour === 0) {
      const newHour = "12";
      timeArray[0] = newHour;
    } else if (hour <= 12) {
      const newHour = hour.toString();
      timeArray[0] = newHour;
    } else {
      const newHour = (hour - 12).toString();
      timeArray[0] = newHour;
    }

    return `${timeArray[0]}:${timeArray[1]} ${m}`;
  };

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="tasks-page">
      <h1>Tasks</h1>
      {tasks
        ? tasks.map((task) => (
            <Card onClick={toggle} className="tasks-page-task-card">
              <Table borderless>
                <thead>
                  <tr>
                    <th>Task</th>
                    <th>Date</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{task.taskDescription}</td>
                    <td>{dateConverter(task.taskDate)}</td>
                    <td>{timeConverter(task.taskTime)}</td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          ))
        : null}
        <Modal isOpen={isOpen} toggle={toggle}>
          <ModalHeader toggle={toggle}>Task</ModalHeader>
          <ModalBody>

          </ModalBody>
          <ModalFooter>
            <Button color="danger">Edit</Button>
            <Button color="success">Complete</Button>
          </ModalFooter>
        </Modal>
    </div>
  );
};

export default Task;
