import React, { useState } from 'react';
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button,
    Modal,
    Form,
    ModalHeader,
    ModalBody,
    FormGroup,
    Label,
    Input
} from 'reactstrap';
import { NavLink as RRNavLink} from 'react-router-dom';
import Logo from '../../assets/logo.png';
import UserIcon from '../../assets/user.svg';
import axiosWithAuth from '../Login/withAuth';
import jwt from 'jsonwebtoken';
import { dbUrl } from '../../actions/index';
import Sidebar from './EmployeeSidebar';
import Footer from './Footer';
import '../../styles/employee-navbar.scss';

const EmployeeNavbar = () => {
    const [name, setName] = useState({first: "Cory", last: "Hale"});
    const [isOpen, setIsOpen] = useState(false);

    const toggle = () => {
        setIsOpen(!isOpen);
    }

    const logout = e => {
        e.preventDefault();
        localStorage.removeItem('token');
        this.props.history.push('/');
    }

    const closeBtn = <Button className="close" onClick={toggle}>&times;</Button>;

    return (
        <div className="nav-container">
            <Navbar className="employee-navbar" expand="md">
                <NavbarBrand href="/">
                    <div className="logo-box">
                        <img className="logo" src={ Logo } alt="rocky mountain lifts" />
                    </div>
                </NavbarBrand>
                <Nav className="ml-auto" navbar>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard" exact>
                            Tasks
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/workorders">
                            Work Orders
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/customers">
                            Customers
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/files">
                            Files
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={RRNavLink} className="nav-link" activeClassName="active" to="/e_dashboard/employees">
                            Employees
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <div className="divider" />
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link" onClick={toggle}> 
                            {name.first} {name.last} 
                        </NavLink> 
                    </NavItem>
                </Nav>
            </Navbar>
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle} close={closeBtn} />
                <ModalBody>
                    <Button outline color="danger" onClick={logout}>Logout</Button>
                </ModalBody>
            </Modal>

        </div>
    )
}

export default EmployeeNavbar;
