import { dbUrl } from './index';
import axiosWithAuth from '../components/Login/withAuth';

export const GET_JOB_TITLES_START = 'GET_JOB_TITLES_START';
export const GET_JOB_TITLES_SUCCESS = 'GET_JOB_TITLES_SUCCESS';
export const GET_JOB_TITLES_FAILURE = 'GET_JOB_TITLES_FAILURE';

export const getJobTitles = () => async dispatch => {
    dispatch({ type: GET_JOB_TITLES_START });

    return await axiosWithAuth()
        .get(`${dbUrl}/jobTitles`)
        .then(res => {
            dispatch({
                type: GET_JOB_TITLES_SUCCESS,
                payload: res.data
            });
        })
        .catch(err => {
            dispatch({
                type: GET_JOB_TITLES_FAILURE,
                payload: err
            });
        });
};
