import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import EmployeesTable from "./EmployeesTable";

// import '../../styles/employee-dashboard.scss';

const CurrentEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [currentEmployees, setCurrentEmployees] = useState([]);
  const getEmployees = useSelector(
    (state) => state.getEmployeesReducer.employees
  );

  useEffect(() => {
    setEmployees(getEmployees.employees);
  }, [getEmployees]);

  useEffect(() => {
    const activeEmployees = [];

    if (employees) {
      employees.filter((employee) => {
        if (employee.active === true) {
          activeEmployees.push(employee);
        }
      });
    }

    setCurrentEmployees(activeEmployees);
  }, [employees]);

  return (
    <div className="employees-page">
      <h1>Current Employees</h1>
      <EmployeesTable props={currentEmployees} />
    </div>
  );
};

export default CurrentEmployees;
