import React, {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { getCustomerNotes } from '../../actions/getCustomerNotes';

const CustomerPageNotesTab = () => {
    const [notes, setNotes] = useState([]);
    const getNotesFromState = useSelector(state => state.getCustomerNotesReducer.notes);
    const [isOpen, setIsOpen] = useState(false);
    const [note, setNote] = useState({
        note: '',
        createdAt: '',
        createdBy: ''
    });
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const customerId = pathArray[pathArray.length - 1];
        dispatch(getCustomerNotes(customerId));
    }, []);

    useEffect(() => {
        if(getNotesFromState) {
            setNotes(getNotesFromState);
        };
    }, [getNotesFromState]);

    const toggle = () => {
        if(isOpen) {
            setNote({
                note: '',
                createdAt: '',
                createdBy: ''
            });
        };
        setIsOpen(!isOpen);
    };

    const handleChange = e => {
        setNote({
            ...note,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = e => {  // Fix the submit handler
        e.preventDefault();
        console.log("Note Submitted");
    };

    return (
        <div className="customer-page-note-tab">
            {notes ? notes.notes ? notes.notes.map(note => (
                <Card className="note-card">
                    <p>{note.note}</p>
                    <div>
                        <p>Created By: <span>{(note) => {  // Make sure note shows name of creator
                            const name = employeeFinder(note.createdBy);
                            console.log(name)
                        }}</span></p>
                        <p>Created At: {note.createdAt}</p>  {/* Fix time format */}
                    </div>
                </Card>
            )) : null : null}
            <Button outline color="success" onClick={toggle}>Add Note</Button>
            <Modal isOpen={isOpen} toggle={toggle}>
                    <ModalHeader toggle={toggle}>Add Note</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={handleSubmit}>
                            <FormGroup>
                                <Label for="note">Note</Label>
                                <Input type="textarea" name="note" onChange={handleChange} />
                            </FormGroup>
                        </Form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="success" onClick={handleSubmit}>Submit</Button>
                    </ModalFooter>
                </Modal>
        </div>
    );
};

export default CustomerPageNotesTab;
